import React, { useState, useEffect, useRef } from 'react';

interface ProfileDetailModalProps {
  isOpen: boolean;
  onClose: () => void;
  profile: {
    id: string;
    name: string;
    avatar: string;
    gender?: string;
    age?: string;
    job?: string;
    info?: string;
    habit?: string;
  };
  editMode?: boolean;
  onSave?: () => void; // 저장 후 목록 갱신용 콜백
}

export default function ProfileDetailModal({ isOpen, onClose, profile, editMode = false, onSave }: ProfileDetailModalProps) {
  const [isEdit, setIsEdit] = useState(editMode);
  const [name, setName] = useState(profile.name || "");
  const [avatar, setAvatar] = useState(profile.avatar || "/imgdefault.jpg");
  const [gender, setGender] = useState(profile.gender || "밝히지 않음");
  const [age, setAge] = useState(profile.age || "");
  const [job, setJob] = useState(profile.job || "");
  const [info, setInfo] = useState(profile.info || "");
  const [habit, setHabit] = useState(profile.habit || "");
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    setName(profile.name || "");
    setAvatar(profile.avatar || "/imgdefault.jpg");
    setGender(profile.gender || "밝히지 않음");
    setAge(profile.age || "");
    setJob(profile.job || "");
    setInfo(profile.info || "");
    setHabit(profile.habit || "");
    setIsEdit(editMode);
  }, [profile, editMode]);

  if (!isOpen) return null;

  const handleSave = async () => {
    const payload = { 
      name, 
      avatar, 
      gender, 
      age: age && !isNaN(Number(age)) ? Number(age) : null, 
      job, 
      info, 
      habit 
    };
    const res = await fetch(`/api/persona/${profile.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    const data = await res.json();
    if (data.ok) {
      setIsEdit(false);
      if (onSave) onSave();
      onClose();
    } else {
      alert("수정 실패: " + data.error);
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: 20,
        padding: 24,
        width: '90%',
        maxWidth: 400,
        position: 'relative'
      }}>
        <button
          onClick={onClose}
          style={{
            position: 'absolute',
            right: 16,
            top: 16,
            background: 'none',
            border: 'none',
            fontSize: 24,
            cursor: 'pointer',
            color: '#666'
          }}
        >
          ×
        </button>
        {isEdit ? (
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div style={{ position: 'relative', marginBottom: 16 }}>
              <img
                src={avatar || "/imgdefault.jpg"}
                alt={name}
                style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', border: '2px solid #eee', cursor: 'pointer' }}
                onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = '/imgdefault.jpg'; }}
                onClick={() => fileInputRef.current?.click()}
              />
              <button
                onClick={() => fileInputRef.current?.click()}
                style={{
                  position: 'absolute',
                  right: 0,
                  bottom: 0,
                  background: 'var(--color-point)',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '50%',
                  width: 32,
                  height: 32,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: 22,
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
                }}
                aria-label="프로필 사진 변경"
              >
                +
              </button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                style={{ display: 'none' }}
                onChange={e => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onload = (ev) => setAvatar(ev.target?.result as string);
                    reader.readAsDataURL(file);
                  }
                }}
              />
            </div>
            {/* 이름 */}
            <input value={name} onChange={e => setName(e.target.value)} placeholder="이름" style={{ width: '100%', marginBottom: 8, padding: 8, borderRadius: 8, border: '1px solid #eee' }} />
            {/* 성별 */}
            <select value={gender} onChange={e => setGender(e.target.value)} style={{ width: '100%', marginBottom: 8, padding: 8, borderRadius: 8, border: '1px solid #eee' }}>
              <option value="남성">남성</option>
              <option value="여성">여성</option>
              <option value="밝히지 않음">밝히지 않음</option>
            </select>
            {/* 나이 */}
            <input value={age} onChange={e => setAge(e.target.value)} placeholder="나이" style={{ width: '100%', marginBottom: 8, padding: 8, borderRadius: 8, border: '1px solid #eee' }} />
            {/* 직업 */}
            <input value={job} onChange={e => setJob(e.target.value)} placeholder="직업" style={{ width: '100%', marginBottom: 8, padding: 8, borderRadius: 8, border: '1px solid #eee' }} />
            {/* 기본 정보 */}
            <textarea value={info} onChange={e => setInfo(e.target.value)} placeholder="기본 정보" style={{ width: '100%', marginBottom: 8, padding: 8, borderRadius: 8, border: '1px solid #eee' }} />
            {/* 습관 */}
            <textarea value={habit} onChange={e => setHabit(e.target.value)} placeholder="습관" style={{ width: '100%', marginBottom: 8, padding: 8, borderRadius: 8, border: '1px solid #eee' }} />
            <button onClick={handleSave} style={{ width: '100%', background: '#ff4081', color: '#fff', border: 'none', borderRadius: 12, padding: 14, fontWeight: 700, fontSize: 18, marginTop: 8, cursor: 'pointer' }}>저장</button>
          </div>
        ) : (
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <img
              src={profile.avatar || "/imgdefault.jpg"}
              alt={profile.name}
              style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', marginBottom: 16, border: '2px solid #eee' }}
              onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = '/imgdefault.jpg'; }}
            />
            <div style={{ fontWeight: 700, fontSize: 24, marginBottom: 8 }}>{profile.name}</div>
            {(profile.gender || profile.age || profile.job) && (
              <div style={{ color: '#888', fontSize: 16, marginBottom: 8 }}>
                {[profile.gender, profile.age, profile.job].filter(Boolean).join(' · ')}
              </div>
            )}
            {profile.info && (
              <div style={{ color: '#222', fontSize: 16, marginBottom: 16, textAlign: 'center' }}>
                {profile.info}
              </div>
            )}
            {profile.habit && (
              <div style={{ color: '#888', fontSize: 15, marginBottom: 16 }}>
                습관: {profile.habit}
              </div>
            )}
            <button onClick={() => setIsEdit(true)} style={{ width: '100%', background: '#ff4081', color: '#fff', border: 'none', borderRadius: 12, padding: 14, fontWeight: 700, fontSize: 18, marginTop: 8, cursor: 'pointer' }}>수정</button>
          </div>
        )}
      </div>
    </div>
  );
}
