import React from "react";

export interface Msg {
  sender: "user" | "ai";
  text: string;
  avatar?: string;
}

interface Props {
  message: Msg;
  onProfileClick?: () => void;
}

export default function MessageBubble({ message, onProfileClick }: Props) {
  const isUser = message.sender === "user";
  return (
    <div style={{ display: 'flex', flexDirection: isUser ? 'row-reverse' : 'row', alignItems: 'flex-end', marginBottom: 10 }}>
      {!isUser && message.avatar && (
        <img
          src={message.avatar}
          alt="ai"
          style={{ width: 40, height: 40, borderRadius: '50%', marginRight: 10, background: '#222', objectFit: 'cover', cursor: onProfileClick ? 'pointer' : 'default', border: '2px solid #fff' }}
          onClick={onProfileClick}
          onError={e => { e.currentTarget.src = "/imagedefault.jpg"; }}
        />
      )}
      <div
        style={{
          maxWidth: 320,
          padding: isUser ? '13px 18px' : '13px 18px',
          borderRadius: 18,
          background: isUser ? 'linear-gradient(90deg, #ff4081 80%, #ffb3d1 100%)' : 'rgba(255,255,255,0.08)',
          color: isUser ? '#fff' : '#fff',
          fontSize: 17,
          fontWeight: 400,
          marginLeft: isUser ? 0 : 4,
          marginRight: isUser ? 4 : 0,
          boxShadow: isUser ? '0 2px 8px rgba(255,64,129,0.08)' : '0 2px 8px rgba(0,0,0,0.10)',
          border: isUser ? 'none' : '1.5px solid #fff2',
          wordBreak: 'break-word',
        }}
      >
        {message.text}
      </div>
    </div>
  );
}
