import React, { useEffect, useState, useRef } from "react";
import BottomNav from "../components/BottomNav";
import { useNavigate } from "react-router-dom";
import ProfileDetailModal from "../components/ProfileDetailModal";
import DefaultProfileImage from "../components/DefaultProfileImage";
import { FiSettings } from "react-icons/fi";

interface Character {
  id: number;
  profileImg: string;
  name: string;
}

interface Persona {
  id: string;
  name: string;
  avatar: string;
  gender?: string;
  age?: string;
  job?: string;
  info?: string;
  habit?: string;
}

// ÌîÑÎ°úÌïÑ Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú ÏÉÅÏàòÎ°ú ÏßÄÏ†ï
const DEFAULT_PROFILE_IMG = "/imgdefault.jpg";

function MyPageSkeleton() {
  return (
    <div style={{ padding: "24px 20px 0 20px" }}>
      <div style={{ background: "#f3f3f3", borderRadius: 20, margin: 20, padding: 20, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div style={{ width: 60, height: 60, borderRadius: "50%", background: "#e0e0e0", marginRight: 16, animation: "pulse 1.2s infinite" }} />
        <div style={{ flex: 1 }}>
          <div style={{ width: 120, height: 20, background: "#e0e0e0", borderRadius: 8, marginBottom: 8, animation: "pulse 1.2s infinite" }} />
          <div style={{ width: 80, height: 16, background: "#e0e0e0", borderRadius: 8, animation: "pulse 1.2s infinite" }} />
        </div>
        <div style={{ width: 56, height: 32, background: "#e0e0e0", borderRadius: 16, animation: "pulse 1.2s infinite" }} />
      </div>
      <div style={{ width: 120, height: 18, background: "#e0e0e0", borderRadius: 8, margin: "24px 0 16px 0", animation: "pulse 1.2s infinite" }} />
      <div style={{ background: "#f3f3f3", borderRadius: 16, margin: "16px 0", padding: 16, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div style={{ width: 48, height: 48, borderRadius: "50%", background: "#e0e0e0", marginRight: 16, animation: "pulse 1.2s infinite" }} />
        <div style={{ flex: 1 }}>
          <div style={{ width: 80, height: 16, background: "#e0e0e0", borderRadius: 8, marginBottom: 8, animation: "pulse 1.2s infinite" }} />
        </div>
        <div style={{ width: 40, height: 24, background: "#e0e0e0", borderRadius: 12, animation: "pulse 1.2s infinite" }} />
      </div>
      <style>{`@keyframes pulse { 0% { opacity: 1; } 50% { opacity: 0.5; } 100% { opacity: 1; } }`}</style>
    </div>
  );
}

export default function MyPage() {
  const navigate = useNavigate();
  const [characters, setCharacters] = useState<Character[]>([]);
  const [personas, setPersonas] = useState<Persona[]>([]);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<Persona | null>(null);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showUserProfileEditModal, setShowUserProfileEditModal] = useState(false);
  const [userProfileImg, setUserProfileImg] = useState<string>("/imgdefault.jpg");
  const [userName, setUserName] = useState<string>("user_74127");
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    // Î©ÄÌã∞ÌîÑÎ°úÌïÑ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    fetch(`/api/persona?userId=user_74127`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setPersonas(data.personas);
      });

    // ÎÇ¥ Ï∫êÎ¶≠ÌÑ∞ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    fetch(`/api/character?userId=user_74127`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setCharacters(data.characters);
      });

    // ÏÇ¨Ïö©Ïûê ÌîÑÎ°úÌïÑ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ (localStorageÏóêÏÑú)
    const savedUserProfile = localStorage.getItem('userProfile');
    if (savedUserProfile) {
      const profile = JSON.parse(savedUserProfile);
      setUserProfileImg(profile.avatar || "/imgdefault.jpg");
      setUserName(profile.name || "user_74127");
    }
  }, []);

  const handleProfileClick = (profile: Persona) => {
    setSelectedProfile(profile);
    setShowProfileModal(true);
  };

  const handleUserProfileImageClick = () => {
    fileInputRef.current?.click();
  };

  const handleUserProfileImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        const newProfileImg = ev.target?.result as string;
        setUserProfileImg(newProfileImg);
        // localStorageÏóê Ï†ÄÏû•
        const userProfile = {
          name: userName,
          avatar: newProfileImg
        };
        localStorage.setItem('userProfile', JSON.stringify(userProfile));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUserNameSave = () => {
    // localStorageÏóê Ï†ÄÏû•
    const userProfile = {
      name: userName,
      avatar: userProfileImg
    };
    localStorage.setItem('userProfile', JSON.stringify(userProfile));
    setShowUserProfileEditModal(false);
  };

  return (
    <div style={{ background: "var(--color-bg)", minHeight: "100vh", paddingBottom: 80 }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: "24px 20px 0 20px" }}>
        <span style={{ fontWeight: 700, fontSize: 24 }}>MY</span>
        <button
          onClick={() => setShowSettingsModal(true)}
          style={{ background: 'none', border: 'none', cursor: 'pointer', padding: 4 }}
          aria-label="ÏÑ§Ï†ï Î∞è Î°úÍ∑∏ÏïÑÏõÉ"
        >
          <FiSettings size={26} color="#fff" />
        </button>
      </div>
      <div style={{
        background: "var(--color-card)",
        borderRadius: 20,
        margin: 20,
        padding: 20,
        boxShadow: "0 2px 8px rgba(0,0,0,0.03)",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between"
      }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={userProfileImg}
            alt="ÌîÑÎ°úÌïÑ"
            style={{ width: 60, height: 60, borderRadius: "50%", marginRight: 16, objectFit: "cover", cursor: "pointer" }}
            onClick={() => handleProfileClick({
              id: "user_74127",
              name: userName,
              avatar: userProfileImg
            })}
            onError={e => {
              if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
              e.currentTarget.onerror = null;
              e.currentTarget.src = DEFAULT_PROFILE_IMG;
            }}
          />
          <div>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{userName}</div>
            <div style={{ color: "var(--color-subtext)", fontSize: 15, marginTop: 4 }}>Î≥¥Ïú† Ïûº</div>
          </div>
        </div>
        <div style={{ textAlign: "right" }}>
          <button 
            onClick={() => setShowUserProfileEditModal(true)}
            style={{
              background: "#f5f5f5",
              border: "none",
              borderRadius: 16,
              padding: "8px 18px",
              fontWeight: 600,
              fontSize: 15,
              marginBottom: 16,
              cursor: "pointer"
            }}
          >
            ÎÇ¥ ÌîÑÎ°úÌïÑ
          </button>
          <div style={{ fontWeight: 700, fontSize: 18, color: "#ff4081" }}>35 <span role="img" aria-label="Ïûº">üçì</span></div>
        </div>
      </div>
      {/* Î©ÄÌã∞ÌîÑÎ°úÌïÑ Î¶¨Ïä§Ìä∏ */}
      {personas.length > 1 && (
        <div style={{
          marginTop: 16,
          padding: "0 20px"
        }}>
          <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 8 }}>Î©ÄÌã∞ÌîÑÎ°úÌïÑ</div>
          {personas.filter(p => p.id !== "user_74127").map(p => (
            <div key={p.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card-alt)", borderRadius: 12, padding: 10, marginBottom: 10 }}>
              {p.avatar ? (
                <img
                  src={p.avatar || DEFAULT_PROFILE_IMG}
                  alt={p.name}
                  style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, objectFit: "cover", cursor: "pointer" }}
                  onClick={() => handleProfileClick(p)}
                  onError={e => {
                    if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
                    e.currentTarget.onerror = null;
                    e.currentTarget.src = DEFAULT_PROFILE_IMG;
                  }}
                />
              ) : (
                <div onClick={() => handleProfileClick(p)} style={{ marginRight: 12, cursor: "pointer" }}>
                  <DefaultProfileImage />
                </div>
              )}
              <span style={{ fontWeight: 600, fontSize: 16 }}>{p.name}</span>
              <button style={{ marginLeft: "auto", color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={() => navigate(`/profile/${p.id}`)}>ÏàòÏ†ï</button>
              <button style={{ color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={async () => {
                if (window.confirm("Ï†ïÎßêÎ°ú ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) {
                  await fetch(`/api/persona/${p.id}`, { method: "DELETE" });
                  setPersonas(prev => prev.filter(x => x.id !== p.id));
                }
              }}>ÏÇ≠Ï†ú</button>
            </div>
          ))}
        </div>
      )}
      <div style={{ padding: "0 20px", fontWeight: 700, fontSize: 18, marginTop: 24 }}>ÎÇ¥ Ï∫êÎ¶≠ÌÑ∞</div>
      <div style={{ padding: "0 20px" }}>
        {characters.length === 0 ? (
          <div style={{ color: "var(--color-subtext)" }}>ÏïÑÏßÅ ÎßåÎì† Ï∫êÎ¶≠ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</div>
        ) : (
          characters.map(char => (
            <div key={char.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card)", borderRadius: 12, padding: 12, marginBottom: 12 }}>
              <img
                src={char.profileImg || DEFAULT_PROFILE_IMG}
                alt={char.name}
                style={{ width: 48, height: 48, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
                onError={e => {
                  if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
                  e.currentTarget.onerror = null;
                  e.currentTarget.src = DEFAULT_PROFILE_IMG;
                }}
              />
              <span style={{ fontWeight: 600, fontSize: 16 }}>{char.name}</span>
              <button style={{ marginLeft: "auto", color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={() => navigate(`/character/${char.id}`)}>ÏàòÏ†ï</button>
            </div>
          ))
        )}
      </div>
      <BottomNav />

      {/* Ïú†Ï†Ä ÌîÑÎ°úÌïÑ ÏàòÏ†ï Î™®Îã¨ */}
      {showUserProfileEditModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 2000
        }}>
          <div style={{ background: 'var(--color-card)', borderRadius: 20, padding: 32, width: '90%', maxWidth: 400, position: 'relative', boxShadow: '0 2px 16px rgba(0,0,0,0.18)' }}>
            <button
              onClick={() => setShowUserProfileEditModal(false)}
              style={{ position: 'absolute', right: 16, top: 16, background: 'none', border: 'none', fontSize: 24, cursor: 'pointer', color: '#fff' }}
              aria-label="Îã´Í∏∞"
            >√ó</button>
            <div style={{ fontWeight: 700, fontSize: 22, color: '#fff', marginBottom: 32, textAlign: 'center' }}>ÌîÑÎ°úÌïÑ ÏàòÏ†ï</div>

            {/* ÌîÑÎ°úÌïÑ Ïù¥ÎØ∏ÏßÄ */}
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
              <div style={{ position: 'relative' }}>
                <img
                  src={userProfileImg}
                  alt="ÌîÑÎ°úÌïÑ"
                  style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', border: '3px solid #fff' }}
                  onError={e => {
                    if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
                    e.currentTarget.onerror = null;
                    e.currentTarget.src = DEFAULT_PROFILE_IMG;
                  }}
                />
                <button
                  onClick={handleUserProfileImageClick}
                  style={{
                    position: 'absolute',
                    right: 0,
                    bottom: 0,
                    background: '#ff4081',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '50%',
                    width: 32,
                    height: 32,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: 18,
                    cursor: 'pointer',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
                  }}
                  aria-label="ÌîÑÎ°úÌïÑ ÏÇ¨ÏßÑ Î≥ÄÍ≤Ω"
                >
                  ‚úèÔ∏è
                </button>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleUserProfileImageChange}
                />
              </div>
            </div>

            {/* Ïù¥Î¶Ñ ÏûÖÎ†• */}
            <div style={{ marginBottom: 24 }}>
              <label style={{ display: 'block', fontWeight: 600, fontSize: 16, color: '#fff', marginBottom: 8 }}>Ïù¥Î¶Ñ</label>
              <input
                type="text"
                value={userName}
                onChange={(e) => setUserName(e.target.value)}
                style={{
                  width: '100%',
                  padding: '12px 16px',
                  borderRadius: 12,
                  border: '1px solid #444',
                  background: '#333',
                  color: '#fff',
                  fontSize: 16,
                  outline: 'none'
                }}
                placeholder="Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              />
            </div>

            {/* Ï†ÄÏû• Î≤ÑÌäº */}
            <button
              onClick={handleUserNameSave}
              style={{
                width: '100%',
                background: '#ff4081',
                color: '#fff',
                border: 'none',
                borderRadius: 12,
                padding: 16,
                fontWeight: 700,
                fontSize: 18,
                cursor: 'pointer'
              }}
            >
              Ï†ÄÏû•
            </button>
          </div>
        </div>
      )}

      {/* ÏÑ§Ï†ï Î™®Îã¨ */}
      {showSettingsModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 2000
        }}>
          <div style={{ background: '#222', borderRadius: 20, padding: 32, width: '90%', maxWidth: 340, position: 'relative', boxShadow: '0 2px 16px rgba(0,0,0,0.18)', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <button
              onClick={() => setShowSettingsModal(false)}
              style={{ position: 'absolute', right: 16, top: 16, background: 'none', border: 'none', fontSize: 24, cursor: 'pointer', color: '#fff' }}
              aria-label="Îã´Í∏∞"
            >√ó</button>
            <div style={{ fontWeight: 700, fontSize: 22, color: '#fff', marginBottom: 32 }}>ÏÑ§Ï†ï</div>
            <button
              onClick={() => {
                localStorage.clear();
                sessionStorage.clear();
                // Íµ¨Í∏Ä Í≥ÑÏ†ïÍπåÏßÄ ÏôÑÏ†Ñ Î°úÍ∑∏ÏïÑÏõÉ ÌõÑ /loginÏúºÎ°ú Ïù¥Îèô
                window.location.href = "https://accounts.google.com/Logout?continue=https://appengine.google.com/_ah/logout?continue=" + encodeURIComponent(window.location.origin + "/login");
              }}
              style={{ width: '100%', background: '#ff4081', color: '#fff', border: 'none', borderRadius: 12, padding: 16, fontWeight: 700, fontSize: 18, cursor: 'pointer', marginBottom: 8 }}
            >Î°úÍ∑∏ÏïÑÏõÉ</button>
          </div>
        </div>
      )}
      {/* ÌîÑÎ°úÌïÑ ÏÉÅÏÑ∏ Î™®Îã¨ */}
      <ProfileDetailModal
        isOpen={showProfileModal}
        onClose={() => setShowProfileModal(false)}
        profile={selectedProfile || {
          id: "",
          name: "",
          avatar: ""
        }}
      />
    </div>
  );
}
