import React, { useEffect, useState } from "react";
import BottomNav from "../components/BottomNav";
import { useNavigate } from "react-router-dom";
import ProfileEditModal from "../components/ProfileEditModal";

interface Character {
  id: number;
  profileImg: string;
  name: string;
  age: number;
  job: string;
  oneLiner: string;
  backgroundImg?: string;
  firstScene?: string;
  firstMessage?: string;
  category?: string;
  userId?: string;
  selectedTags?: string[];
}

interface Persona {
  id: string;
  name: string;
  avatar: string;
}

const DEFAULT_PERSONAS: Persona[] = [
  { id: "user_74127", name: "user_74127", avatar: "/avatars/user.jpg" },
  // 추가 페르소나 예시
];

// 프로필 이미지 경로 상수로 지정
const DEFAULT_PROFILE_IMG = "/imgdefault.jpg"; // 실제 파일 경로로 교체 필요

function ForYouSkeleton() {
  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "70vh" }}>
      <div style={{
        background: "#f3f3f3",
        borderRadius: 20,
        padding: 32,
        width: 320,
        maxWidth: "90vw",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        boxShadow: "0 2px 16px rgba(0,0,0,0.07)"
      }}>
        <div style={{ width: 100, height: 100, borderRadius: "50%", background: "#e0e0e0", marginBottom: 20, animation: "pulse 1.2s infinite" }} />
        <div style={{ width: 120, height: 24, borderRadius: 8, background: "#e0e0e0", marginBottom: 12, animation: "pulse 1.2s infinite" }} />
        <div style={{ width: 180, height: 18, borderRadius: 8, background: "#e0e0e0", marginBottom: 16, animation: "pulse 1.2s infinite" }} />
        <div style={{ width: "100%", height: 48, borderRadius: 12, background: "#e0e0e0", marginTop: 16, animation: "pulse 1.2s infinite" }} />
      </div>
      <style>
        {`@keyframes pulse { 0% { opacity: 1; } 50% { opacity: 0.5; } 100% { opacity: 1; } }`}
      </style>
    </div>
  );
}

// CharacterDetailModal: 첨부 이미지 스타일 전체화면 상세
function CharacterDetailModal({ isOpen, onClose, character }: { isOpen: boolean, onClose: () => void, character: any }) {
  const [tab, setTab] = React.useState(0);

  if (!isOpen || !character) return null;

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.72)', zIndex: 3000,
      display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-start', overflowY: 'auto'
    }}>
      <div style={{ width: '100%', maxWidth: 430, margin: '0 auto', background: 'var(--color-bg)', minHeight: '100vh', borderRadius: 0, position: 'relative', boxShadow: '0 2px 16px rgba(0,0,0,0.18)' }}>
        {/* 상단 프사 이미지 3:4 비율 */}
        <div style={{ width: '100%', aspectRatio: '3/4', background: '#222', display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative' }}>
          <img
            src={character.profileImg || DEFAULT_PROFILE_IMG}
            alt={character.name}
            style={{ width: '100%', height: '100%', objectFit: 'contain', objectPosition: 'center', zIndex: 1, background: '#222' }}
            onError={e => {
              if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
              e.currentTarget.onerror = null;
              e.currentTarget.src = DEFAULT_PROFILE_IMG;
            }}
          />
        </div>
        {/* 카테고리, by */}
        <div style={{ position: 'absolute', left: 24, top: 24, display: 'flex', alignItems: 'center', gap: 10, zIndex: 2 }}>
          <span style={{ background: 'var(--color-point)', color: '#fff', fontWeight: 700, fontSize: 13, borderRadius: 8, padding: '4px 10px' }}>{character.category || '카테고리 없음'}</span>
          <span style={{ color: '#fff', fontWeight: 500, fontSize: 15, opacity: 0.85 }}>by. {character.userId || '-'}</span>
        </div>
        {/* 태그 */}
        <div style={{ padding: '16px 20px 0 20px', display: 'flex', gap: 8, flexWrap: 'wrap' }}>
          {character.selectedTags?.map((tag: string) => (
            <span key={tag} style={{ background: '#ffd6ea', color: '#ff4081', borderRadius: 8, padding: '4px 10px', fontWeight: 600, fontSize: 14 }}>#{tag}</span>
          ))}
        </div>
        {/* 카드 상단: 프로필, 이름, 나이/직업, 첫상황, 첫대사 */}
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '32px 0 0 0' }}>
          <img
            src={character.profileImg || DEFAULT_PROFILE_IMG}
            alt={character.name}
            style={{ width: 64, height: 64, borderRadius: '50%', objectFit: 'cover', border: '3px solid #fff', background: '#eee', marginBottom: 8 }}
            onError={e => {
              if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
              e.currentTarget.onerror = null;
              e.currentTarget.src = DEFAULT_PROFILE_IMG;
            }}
          />
          <div style={{ fontWeight: 700, fontSize: 20, color: '#fff', marginBottom: 2 }}>{character.name}</div>
          <div style={{ fontSize: 15, color: '#ccc', marginBottom: 10 }}>{character.age}살 · {character.job}</div>
          {character.firstScene && (
            <div style={{ fontSize: 15, color: '#bbb', marginBottom: 4, textAlign: 'center', maxWidth: 320 }}>{character.firstScene}</div>
          )}
          {character.firstMessage && (
            <div style={{ fontSize: 17, color: '#fff', fontWeight: 500, background: 'rgba(255,255,255,0.08)', borderRadius: 10, padding: '8px 16px', marginBottom: 8, textAlign: 'center', maxWidth: 320 }}>{character.firstMessage}</div>
          )}
        </div>
        {/* 탭 */}
        <div style={{ display: 'flex', margin: '24px 0 0 0', borderBottom: '1.5px solid #f5b3d7' }}>
          <div onClick={() => setTab(0)} style={{ flex: 1, textAlign: 'center', padding: 12, fontWeight: 700, color: tab === 0 ? '#ff4081' : '#bbb', borderBottom: tab === 0 ? '2.5px solid #ff4081' : 'none', cursor: 'pointer' }}>서사</div>
          <div onClick={() => setTab(1)} style={{ flex: 1, textAlign: 'center', padding: 12, fontWeight: 700, color: tab === 1 ? '#ff4081' : '#bbb', borderBottom: tab === 1 ? '2.5px solid #ff4081' : 'none', cursor: 'pointer' }}>첫 장면</div>
          <div onClick={() => setTab(2)} style={{ flex: 1, textAlign: 'center', padding: 12, fontWeight: 700, color: tab === 2 ? '#ff4081' : '#bbb', borderBottom: tab === 2 ? '2.5px solid #ff4081' : 'none', cursor: 'pointer' }}>커뮤니티</div>
        </div>
        {/* 탭 내용 */}
        <div style={{ padding: '20px' }}>
          {tab === 0 && (
            <>
              <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 6 }}>{character.name}의 서사</div>
              <div style={{ background: 'var(--color-card)', borderRadius: 12, padding: 16, fontWeight: 500, color: 'var(--color-text)', fontSize: 16 }}>{character.firstScene || '등록된 서사가 없습니다.'}</div>
            </>
          )}
          {tab === 1 && (
            <>
              <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 6 }}>첫 상황</div>
              <div style={{ background: 'var(--color-card)', borderRadius: 12, padding: 16, fontWeight: 500, color: 'var(--color-text)', fontSize: 16, marginBottom: 16 }}>{character.firstScene || '등록된 첫 상황이 없습니다.'}</div>
              <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 6 }}>채팅 첫 마디</div>
              <div style={{ background: 'var(--color-card)', borderRadius: 12, padding: 16, fontWeight: 500, color: 'var(--color-text)', fontSize: 16 }}>{character.firstMessage || '등록된 첫 마디가 없습니다.'}</div>
            </>
          )}
          {tab === 2 && (
            <>
              <div style={{ background: 'var(--color-card)', borderRadius: 12, padding: 32, fontWeight: 700, color: '#ff4081', fontSize: 18, textAlign: 'center' }}>준비중</div>
            </>
          )}
        </div>
        {/* 하단 고정 채팅하기 버튼 */}
        <button
          style={{
            position: 'fixed', left: 0, right: 0, bottom: 0, height: 56, background: '#ff4081', color: '#fff', fontWeight: 700, fontSize: 20, border: 'none', borderRadius: 0, cursor: 'pointer', zIndex: 10
          }}
          onClick={() => window.location.href = `/chat/${character.id}`}
        >채팅하기</button>
      </div>
    </div>
  );
}

export default function ForYouPage() {
  const [characters, setCharacters] = useState<Character[]>([]);
  const [loading, setLoading] = useState(true);
  const [index, setIndex] = useState(0);
  const [showPersonaModal, setShowPersonaModal] = useState(false);
  const [showPersonaManager, setShowPersonaManager] = useState(false);
  const [showPersonaCreator, setShowPersonaCreator] = useState(false);
  const [personas, setPersonas] = useState<Persona[]>(DEFAULT_PERSONAS);
  const [selectedPersona, setSelectedPersona] = useState<string>(personas[0]?.id || "");
  const [addingPersona, setAddingPersona] = useState(false);
  const [newPersonaName, setNewPersonaName] = useState("");
  const [newPersonaAvatar, setNewPersonaAvatar] = useState<string>("");
  const [creatorProfileImg, setCreatorProfileImg] = useState<string>("");
  const [creatorGender, setCreatorGender] = useState("밝히지 않음");
  const [creatorName, setCreatorName] = useState("");
  const [creatorAge, setCreatorAge] = useState("");
  const [creatorJob, setCreatorJob] = useState("");
  const [creatorInfo, setCreatorInfo] = useState("");
  const [creatorHabit, setCreatorHabit] = useState("");
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<any>(null);
  const [showProfileEditModal, setShowProfileEditModal] = useState(false);
  const [editProfile, setEditProfile] = useState<any>(null);
  const navigate = useNavigate();

  // 프로필 사진 업로드
  const fileInputRef = React.useRef<HTMLInputElement>(null);
  const handleProfileImgClick = () => fileInputRef.current?.click();
  const handleProfileImgChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        setCreatorProfileImg(ev.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  // userId는 임시로 하드코딩
  const userId = "user_74127";

  // 멀티프로필 목록 불러오기 (GET)
  useEffect(() => {
    fetch(`/api/persona?userId=${userId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setPersonas(data.personas);
      });
  }, [showPersonaManager, showPersonaCreator]);

  useEffect(() => {
    fetch("/api/character")
      .then(res => res.json())
      .then(data => {
        if (data.ok) setCharacters(data.characters);
        setLoading(false);
      });
  }, []);

  const handlePrev = () => {
    setIndex((prev) => (prev === 0 ? characters.length - 1 : prev - 1));
  };
  const handleNext = () => {
    setIndex((prev) => (prev === characters.length - 1 ? 0 : prev + 1));
  };

  // 터치 스와이프 지원
  let touchStartX = 0;
  let touchEndX = 0;
  const handleTouchStart = (e: React.TouchEvent) => {
    touchStartX = e.changedTouches[0].screenX;
  };
  const handleTouchEnd = (e: React.TouchEvent) => {
    touchEndX = e.changedTouches[0].screenX;
    if (touchEndX - touchStartX > 50) handlePrev();
    else if (touchStartX - touchEndX > 50) handleNext();
  };

  // 채팅하기 버튼 클릭 시
  const handleChatClick = () => {
    setShowPersonaModal(true);
  };

  // 페르소나 추가
  const handleAddPersona = () => {
    if (!newPersonaName) return;
    setPersonas(prev => [
      ...prev,
      { id: newPersonaName, name: newPersonaName, avatar: newPersonaAvatar || "/avatars/user.jpg" }
    ]);
    setSelectedPersona(newPersonaName);
    setNewPersonaName("");
    setNewPersonaAvatar("");
    setAddingPersona(false);
  };

  // 멀티프로필 생성 완료 (POST)
  const handlePersonaCreate = async () => {
    if (!creatorName) return alert("이름을 입력해주세요");
    const payload = {
      userId,
      name: creatorName,
      avatar: creatorProfileImg || "/avatars/user.jpg",
      gender: creatorGender,
      age: creatorAge,
      job: creatorJob,
      info: creatorInfo,
      habit: creatorHabit,
    };
    const res = await fetch("/api/persona", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    const data = await res.json();
    if (data.ok) {
      setShowPersonaCreator(false);
      setShowPersonaManager(false);
      // 폼 초기화
      setCreatorProfileImg(""); setCreatorGender("밝히지 않음"); setCreatorName(""); setCreatorAge(""); setCreatorJob(""); setCreatorInfo(""); setCreatorHabit("");
    } else {
      alert("프로필 저장 실패: " + data.error);
    }
  };

  // 멀티프로필 삭제 (DELETE)
  const handleDeletePersona = async (id: string) => {
    if (id === userId) return; // 기본 프로필은 삭제 불가
    if (window.confirm("정말로 삭제하시겠습니까?")) {
      await fetch(`/api/persona/${id}`, { method: "DELETE" });
      setPersonas(prev => prev.filter(p => p.id !== id));
      if (selectedPersona === id && personas.length > 1) {
        setSelectedPersona(personas[0].id);
      }
    }
  };

  // 채팅 시작
  const handleStartChat = () => {
    setShowPersonaModal(false);
    // 실제로는 navigate(`/chat/${characters[index].id}?persona=${selectedPersona}`)
    window.location.href = `/chat/${characters[index].id}?persona=${selectedPersona}`;
  };

  // 캐릭터 카드 클릭 시 상세로 이동
  const handleCardClick = (id: number) => {
    navigate(`/character/${id}`);
  };

  // 멀티프로필 관리 모달 내부
  const PersonaManager = () => (
    <div style={{
      position: "fixed", left: 0, top: 0, width: "100vw", height: "100vh", background: "var(--color-card)", zIndex: 2000, display: "flex", flexDirection: "column"
    }}>
      <div style={{ display: "flex", alignItems: "center", padding: "18px 20px 10px 20px", borderBottom: "1px solid #eee" }}>
        <button onClick={() => setShowPersonaManager(false)} style={{ background: "none", border: "none", fontSize: 26, marginRight: 8, cursor: "pointer", color: "#fff" }}>&larr;</button>
        <span style={{ fontWeight: 700, fontSize: 22 }}>멀티프로필</span>
      </div>
      <div style={{ padding: 24 }}>
        {personas.length === 0 && (
          <div style={{ color: "#bbb", fontSize: 16, marginTop: 40 }}>아직 등록된 프로필이 없습니다.</div>
        )}
        {[...personas].sort((a, b) => (a.id === "user_74127" ? -1 : b.id === "user_74127" ? 1 : 0)).map(p => (
          <div key={p.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card-alt)", borderRadius: 12, padding: 14, marginBottom: 14 }}>
            <img
              src={p.avatar || "/imgdefault.jpg"}
              alt={p.name}
              style={{ width: 44, height: 44, borderRadius: "50%", marginRight: 14, objectFit: "cover" }}
              onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = "/imgdefault.jpg"; }}
            />
            <span style={{ fontWeight: 600, fontSize: 17 }}>{p.name}</span>
            {p.id !== "user_74127" ? (
              <>
                <button onClick={() => handleProfileEdit(p)} style={{ marginLeft: "auto", background: "none", border: "none", color: "#ff4081", fontSize: 18, cursor: "pointer" }}>수정</button>
                <button onClick={() => handleDeletePersona(p.id)} style={{ background: "none", border: "none", color: "#ff4081", fontSize: 22, cursor: "pointer" }}>✕</button>
              </>
            ) : (
              <span style={{ marginLeft: "auto", color: "#bbb", fontSize: 15 }}>기본 프로필</span>
            )}
          </div>
        ))}
      </div>
    </div>
  );

  // 멀티프로필 생성 폼 전체화면
  const PersonaCreator = ({
    creatorProfileImg,
    handleProfileImgClick,
    fileInputRef,
    handleProfileImgChange,
    creatorGender,
    setCreatorGender,
    creatorName,
    setCreatorName,
    creatorAge,
    setCreatorAge,
    creatorJob,
    setCreatorJob,
    creatorInfo,
    setCreatorInfo,
    creatorHabit,
    setCreatorHabit,
    handlePersonaCreate,
    setShowPersonaCreator
  }: any) => (
    <div style={{ position: "fixed", left: 0, top: 0, width: "100vw", height: "100vh", background: "var(--color-card)", zIndex: 3000, display: "flex", flexDirection: "column" }}>
      <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", padding: "18px 20px 10px 20px" }}>
        <button onClick={() => setShowPersonaCreator(false)} style={{ background: "none", border: "none", fontSize: 26, cursor: "pointer", color: "#fff" }}>&larr;</button>
        <span></span>
        <button onClick={handlePersonaCreate} style={{ background: "none", border: "none", color: "#ff4081", fontWeight: 700, fontSize: 20, cursor: "pointer" }}>완료</button>
      </div>
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginTop: 16 }}>
        <div style={{ position: "relative", width: 110, height: 110, marginBottom: 16 }}>
          <img
            src={creatorProfileImg || "/imgdefault.jpg"}
            alt="프로필"
            style={{ width: 110, height: 110, borderRadius: "50%", objectFit: "cover", background: "#ffe5e5" }}
          />
          <button
            type="button"
            onClick={handleProfileImgClick}
            style={{
              position: "absolute", right: 0, bottom: 0, background: "#fff", border: "none", borderRadius: "50%", width: 36, height: 36, display: "flex", alignItems: "center", justifyContent: "center", fontSize: 20, boxShadow: "0 2px 8px rgba(0,0,0,0.08)", cursor: "pointer"
            }}
            aria-label="프로필 사진 변경"
          >
            <span role="img" aria-label="edit">✏️</span>
          </button>
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            style={{ display: "none" }}
            onChange={handleProfileImgChange}
          />
        </div>
      </div>
      <div style={{ padding: "0 20px", marginTop: 8 }}>
        {/* 성별 */}
        <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 8 }}>성별</div>
        <div style={{ display: "flex", gap: 18, marginBottom: 18 }}>
          {['남성', '여성', '밝히지 않음'].map(g => (
            <label key={g} style={{ display: "flex", alignItems: "center", fontWeight: 500, fontSize: 16 }}>
              <input type="radio" checked={creatorGender === g} onChange={() => setCreatorGender(g)} style={{ marginRight: 6 }} /> {g}
            </label>
          ))}
        </div>
        {/* 이름 */}
        <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 4 }}>이름 <span style={{ color: '#888', fontWeight: 400, fontSize: 14 }}>(필수)</span></div>
        <input
          placeholder="이름을 입력해주세요"
          value={creatorName}
          onChange={e => {
            const v = e.target.value;
            if (v.length <= 15) setCreatorName(v);
            else setCreatorName(v.slice(0, 15));
          }}
          style={{ width: "100%", borderRadius: 12, border: "1px solid #eee", padding: 14, fontSize: 16, marginBottom: 2, background: '#fafafa' }}
        />
        <div style={{ color: '#bbb', fontSize: 13, textAlign: 'right', marginBottom: 12 }}>{creatorName.length}/15</div>
        {/* 나이 */}
        <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 4 }}>나이</div>
        <input
          placeholder="나이를 입력해주세요"
          value={creatorAge}
          onChange={e => {
            const v = e.target.value;
            if (v.length <= 15) setCreatorAge(v);
            else setCreatorAge(v.slice(0, 15));
          }}
          style={{ width: "100%", borderRadius: 12, border: "1px solid #eee", padding: 14, fontSize: 16, marginBottom: 2, background: '#fafafa' }}
        />
        <div style={{ color: '#bbb', fontSize: 13, textAlign: 'right', marginBottom: 12 }}>{creatorAge.length}/15</div>
        {/* 직업 */}
        <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 4 }}>직업</div>
        <input
          placeholder="직업을 입력해주세요"
          value={creatorJob}
          onChange={e => {
            const v = e.target.value;
            if (v.length <= 15) setCreatorJob(v);
            else setCreatorJob(v.slice(0, 15));
          }}
          style={{ width: "100%", borderRadius: 12, border: "1px solid #eee", padding: 14, fontSize: 16, marginBottom: 2, background: '#fafafa' }}
        />
        <div style={{ color: '#bbb', fontSize: 13, textAlign: 'right', marginBottom: 12 }}>{creatorJob.length}/15</div>
        {/* 기본 정보 */}
        <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 4 }}>기본 정보</div>
        <textarea
          placeholder="외모, 성격 등 기본 정보를 알려주세요"
          value={creatorInfo}
          onChange={e => {
            const v = e.target.value;
            if (v.length <= 500) setCreatorInfo(v);
            else setCreatorInfo(v.slice(0, 500));
          }}
          style={{ width: "100%", borderRadius: 12, border: "1px solid #eee", padding: 14, fontSize: 16, marginBottom: 2, background: '#fafafa', resize: 'none' }}
          rows={3}
        />
        <div style={{ color: '#bbb', fontSize: 13, textAlign: 'right', marginBottom: 12 }}>{creatorInfo.length}/500</div>
        {/* 습관적 말과 행동 */}
        <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 4 }}>습관적인 말과 행동</div>
        <textarea
          placeholder="예시를 입력해주세요"
          value={creatorHabit}
          onChange={e => {
            const v = e.target.value;
            if (v.length <= 500) setCreatorHabit(v);
            else setCreatorHabit(v.slice(0, 500));
          }}
          style={{ width: "100%", borderRadius: 12, border: "1px solid #eee", padding: 14, fontSize: 16, marginBottom: 2, background: '#fafafa', resize: 'none' }}
          rows={3}
        />
        <div style={{ color: '#bbb', fontSize: 13, textAlign: 'right', marginBottom: 12 }}>{creatorHabit.length}/500</div>
      </div>
    </div>
  );

  const handleProfileEdit = (profile: any) => {
    setEditProfile(profile);
    setShowProfileEditModal(true);
  };

  const handleProfileSave = async (updatedProfile: any) => {
    try {
      const response = await fetch(`/api/persona/${updatedProfile.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: updatedProfile.name,
          gender: updatedProfile.gender,
          age: updatedProfile.age,
          job: updatedProfile.job,
          info: updatedProfile.info,
          habit: updatedProfile.habit,
          avatar: updatedProfile.avatar || '/imgdefault.jpg'
        }),
      });
      if (response.ok) {
        setPersonas(prev => prev.map(p => p.id === updatedProfile.id ? updatedProfile : p));
        setShowProfileEditModal(false);
        setEditProfile(null);
        alert('프로필이 성공적으로 수정되었습니다.');
      } else {
        throw new Error('프로필 수정에 실패했습니다.');
      }
    } catch (error) {
      alert('프로필 수정 중 오류가 발생했습니다.');
    }
  };

  return (
    <div style={{ background: "var(--color-bg)", minHeight: "100vh", paddingBottom: 80 }}>
      <div style={{ padding: "24px 20px 0 20px", fontWeight: 700, fontSize: 24, textAlign: "center" }}>FOR YOU ♥</div>
      {loading ? (
        <ForYouSkeleton />
      ) : characters.length === 0 ? (
        <div style={{ padding: 20, color: "#888" }}>저장된 캐릭터가 없습니다.</div>
      ) : (
        <div
          style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "70vh", position: "relative" }}
          onTouchStart={handleTouchStart}
          onTouchEnd={handleTouchEnd}
        >
          <button onClick={handlePrev} style={{ position: "absolute", left: 10, top: "50%", transform: "translateY(-50%)", fontSize: 32, background: "none", border: "none", cursor: "pointer", color: "#fff" }} aria-label="이전 캐릭터">&#60;</button>
          <div
            style={{
              position: "relative",
              width: 340,
              maxWidth: "95vw",
              height: 480,
              borderRadius: 28,
              overflow: "hidden",
              boxShadow: "0 2px 16px rgba(0,0,0,0.13)",
              display: "flex",
              flexDirection: "column",
              justifyContent: "flex-end",
              background: "#222"
            }}
          >
            {/* Blurred background */}
            <div style={{
              position: "absolute",
              inset: 0,
              background: `url(${characters[index].backgroundImg || characters[index].profileImg}) center/cover no-repeat`,
              filter: "blur(18px) brightness(0.7)",
              zIndex: 1
            }} />
            {/* Overlay for darkening */}
            <div style={{ position: "absolute", inset: 0, background: "rgba(0,0,0,0.45)", zIndex: 2 }} />
            {/* Profile section */}
            <div style={{ position: "absolute", top: 32, left: 0, width: "100%", zIndex: 3, display: "flex", alignItems: "center", padding: "0 28px" }}>
              <img
                src={characters[index].profileImg || DEFAULT_PROFILE_IMG}
                alt={characters[index].name}
                style={{ width: 64, height: 64, borderRadius: "50%", objectFit: "cover", border: "3px solid #fff", background: "#eee", cursor: "pointer" }}
                onError={e => {
                  if (e.currentTarget.src.endsWith(DEFAULT_PROFILE_IMG)) return;
                  e.currentTarget.onerror = null;
                  e.currentTarget.src = DEFAULT_PROFILE_IMG;
                }}
                onClick={() => {
                  setSelectedProfile({
                    id: characters[index].id?.toString() ?? "",
                    name: characters[index].name,
                    avatar: characters[index].profileImg,
                    age: characters[index].age?.toString(),
                    job: characters[index].job,
                    info: characters[index].oneLiner,
                    // gender, habit 등 필요시 추가
                  });
                  setShowProfileModal(true);
                }}
              />
              <div style={{ marginLeft: 18, display: "flex", flexDirection: "column", justifyContent: "center" }}>
                <div style={{ fontWeight: 700, fontSize: 22, color: "#fff", marginBottom: 2 }}>{characters[index].name}</div>
                <div style={{ color: "#e0e0e0", fontSize: 15, fontWeight: 400 }}>{characters[index].age ? `${characters[index].age}살` : "나이 비공개"} · {characters[index].job || "직업 비공개"}</div>
              </div>
            </div>
            {/* 서사/첫대사 section */}
            <div style={{ position: "relative", zIndex: 4, padding: "0 28px 38px 28px", width: "100%", display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
              {/* 서사(첫장면) */}
              {characters[index].firstScene && (
                <div style={{ color: "#fff", fontSize: 17, fontWeight: 500, marginBottom: 18, textShadow: "0 2px 8px rgba(0,0,0,0.22)", lineHeight: 1.6, textAlign: 'center', maxWidth: 260 }}>
                  {characters[index].firstScene}
                </div>
              )}
              {/* 첫대사(첫마디) */}
              {characters[index].firstMessage && (
                <div style={{ background: "rgba(30,30,30,0.82)", color: "#fff", fontSize: 16, borderRadius: 14, padding: "18px 18px 18px 22px", fontWeight: 400, marginBottom: 10, boxShadow: "0 2px 8px rgba(0,0,0,0.10)", border: "1.5px solid #fff2", textAlign: 'center', maxWidth: 260 }}>
                  {characters[index].firstMessage}
                </div>
              )}
            </div>
            {/* 채팅하기 버튼 */}
            <button
              style={{
                position: "absolute",
                bottom: 18,
                left: 28,
                right: 28,
                height: 48,
                background: "#ff4081",
                color: "#fff",
                fontWeight: 700,
                fontSize: 18,
                border: "none",
                borderRadius: 12,
                cursor: "pointer",
                zIndex: 5,
                boxShadow: "0 2px 8px rgba(0,0,0,0.13)"
              }}
              onClick={handleChatClick}
            >채팅하기</button>
          </div>
          <button onClick={handleNext} style={{ position: "absolute", right: 10, top: "50%", transform: "translateY(-50%)", fontSize: 32, background: "none", border: "none", cursor: "pointer", color: "#fff" }} aria-label="다음 캐릭터">&#62;</button>
        </div>
      )}
      {/* 페르소나 선택 전체화면 모달 */}
      {showPersonaModal && (
        <div style={{
          position: "fixed", left: 0, top: 0, width: "100vw", height: "100vh", background: "var(--color-bg)", zIndex: 1500, display: "flex", flexDirection: "column"
        }}>
          <div style={{ display: "flex", alignItems: "center", padding: "18px 20px 10px 20px", borderBottom: "1px solid var(--color-border)" }}>
            <button onClick={() => setShowPersonaModal(false)} style={{ background: "none", border: "none", fontSize: 26, marginRight: 8, cursor: "pointer", color: "#fff" }}>&larr;</button>
            <span style={{ fontWeight: 700, fontSize: 22, color: "var(--color-text)" }}>채팅 프로필</span>
          </div>
          <div style={{ padding: 24, flex: 1, overflowY: "auto" }}>
            <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 8, color: "var(--color-subtext)" }}>멀티프로필</div>
            <div style={{ color: "var(--color-subtext)", fontSize: 14, marginBottom: 16 }}>직업과 기본 정보를 추가 지원합니다</div>
            <div style={{ fontWeight: 700, fontSize: 16, marginBottom: 8, color: "var(--color-subtext)" }}>유저 프로필</div>
            {personas.map(p => (
              <div key={p.id} style={{ display: "flex", alignItems: "center", background: selectedPersona === p.id ? "#2a1a22" : "var(--color-card)", borderRadius: 12, padding: 10, marginBottom: 10, cursor: "pointer" }} onClick={() => setSelectedPersona(p.id)}>
                <img
                  src={p.avatar || "/imgdefault.jpg"}
                  alt={p.name}
                  style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
                  onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = "/imgdefault.jpg"; }}
                />
                <span style={{ fontWeight: 600, fontSize: 16, color: "var(--color-text)" }}>{p.name}</span>
                {selectedPersona === p.id && <span style={{ marginLeft: "auto", color: "#ff4081", fontSize: 22 }}>✔️</span>}
              </div>
            ))}
            <button onClick={() => setShowPersonaManager(true)} style={{ width: "100%", background: "#18141a", color: "#ff4081", border: "1px solid #ff4081", borderRadius: 8, padding: 10, fontWeight: 700, fontSize: 16, marginTop: 8 }}>+ 프로필 추가/관리</button>
            <button onClick={handleStartChat} style={{ width: "100%", background: "#ff4081", color: "#fff", border: "none", borderRadius: 12, padding: 14, fontWeight: 700, fontSize: 18, marginTop: 18 }}>채팅하기</button>
          </div>
        </div>
      )}
      {/* 멀티프로필 관리 전체화면 모달 */}
      {showPersonaManager && <PersonaManager />}
      {showPersonaCreator && (
        <PersonaCreator
          creatorProfileImg={creatorProfileImg}
          handleProfileImgClick={handleProfileImgClick}
          fileInputRef={fileInputRef}
          handleProfileImgChange={handleProfileImgChange}
          creatorGender={creatorGender}
          setCreatorGender={setCreatorGender}
          creatorName={creatorName}
          setCreatorName={setCreatorName}
          creatorAge={creatorAge}
          setCreatorAge={setCreatorAge}
          creatorJob={creatorJob}
          setCreatorJob={setCreatorJob}
          creatorInfo={creatorInfo}
          setCreatorInfo={setCreatorInfo}
          creatorHabit={creatorHabit}
          setCreatorHabit={setCreatorHabit}
          handlePersonaCreate={handlePersonaCreate}
          setShowPersonaCreator={setShowPersonaCreator}
        />
      )}
      {/* 프로필 상세 모달 */}
      <CharacterDetailModal
        isOpen={showProfileModal}
        onClose={() => setShowProfileModal(false)}
        character={selectedProfile}
      />
      {/* 프로필 수정 모달 */}
      {console.log('모달 조건', showProfileEditModal, editProfile)}
      {showProfileEditModal && editProfile && (
        <ProfileEditModal
          isOpen={showProfileEditModal}
          onClose={() => { setShowProfileEditModal(false); setEditProfile(null); }}
          profileData={{
            id: editProfile.id,
            name: editProfile.name,
            gender: editProfile.gender || '',
            age: editProfile.age ? parseInt(editProfile.age) : 0,
            job: editProfile.job || '',
            info: editProfile.info || '',
            habit: editProfile.habit || '',
            avatar: editProfile.avatar || '/imgdefault.jpg'
          }}
          onSave={handleProfileSave}
        />
      )}
      <BottomNav />
    </div>
  );
}
