import React, { useState, useEffect } from 'react';

interface ProfileEditModalProps {
  isOpen: boolean;
  onClose: () => void;
  profileData: {
    id: string;
    name: string;
    gender: string;
    age: number;
    job: string;
    info: string;
    habit: string;
  };
  onSave: (updatedProfile: any) => void;
}

const ProfileEditModal: React.FC<ProfileEditModalProps> = ({
  isOpen,
  onClose,
  profileData,
  onSave
}) => {
  const [formData, setFormData] = useState({
    name: '',
    gender: '',
    age: '',
    job: '',
    info: '',
    habit: ''
  });

  useEffect(() => {
    if (profileData) {
      setFormData({
        name: profileData.name || '',
        gender: profileData.gender || '',
        age: profileData.age?.toString() || '',
        job: profileData.job || '',
        info: profileData.info || '',
        habit: profileData.habit || ''
      });
    }
  }, [profileData]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name.trim()) {
      alert('이름은 필수 입력 항목입니다.');
      return;
    }

    try {
      const updatedProfile = {
        ...profileData,
        name: formData.name,
        gender: formData.gender,
        age: formData.age ? parseInt(formData.age) : undefined,
        job: formData.job,
        info: formData.info,
        habit: formData.habit
      };

      await onSave(updatedProfile);
      onClose();
    } catch (error) {
      console.error('프로필 수정 중 오류:', error);
      alert('프로필 수정 중 오류가 발생했습니다.');
    }
  };

  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }} onClick={onClose}>
      <div style={{
        background: 'var(--color-card)',
        borderRadius: 16,
        width: '90%',
        maxWidth: 500,
        maxHeight: '90vh',
        overflowY: 'auto'
      }} onClick={(e) => e.stopPropagation()}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '20px 24px',
          borderBottom: '1px solid #333'
        }}>
          <h2 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 600, color: '#fff' }}>프로필 수정</h2>
          <button style={{
            background: 'none',
            border: 'none',
            fontSize: 24,
            cursor: 'pointer',
            color: '#6b7280',
            padding: 0,
            width: 32,
            height: 32,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            borderRadius: 8
          }} onClick={onClose}>×</button>
        </div>

        <form onSubmit={handleSubmit} style={{ padding: 24 }}>
          <div style={{ marginBottom: 20 }}>
            <label htmlFor="name" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>이름 *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
              placeholder="이름을 입력하세요"
              style={{
                width: '100%',
                padding: '12px 16px',
                border: '1px solid #444',
                borderRadius: 8,
                fontSize: 16,
                background: '#333',
                color: '#fff',
                boxSizing: 'border-box'
              }}
            />
          </div>

          <div style={{ marginBottom: 20 }}>
            <label htmlFor="gender" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>성별</label>
            <select
              id="gender"
              name="gender"
              value={formData.gender}
              onChange={handleInputChange}
              style={{
                width: '100%',
                padding: '12px 16px',
                border: '1px solid #444',
                borderRadius: 8,
                fontSize: 16,
                background: '#333',
                color: '#fff',
                boxSizing: 'border-box'
              }}
            >
              <option value="">선택하세요</option>
              <option value="남성">남성</option>
              <option value="여성">여성</option>
              <option value="기타">기타</option>
            </select>
          </div>

          <div style={{ marginBottom: 20 }}>
            <label htmlFor="age" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>나이</label>
            <input
              type="number"
              id="age"
              name="age"
              value={formData.age}
              onChange={handleInputChange}
              placeholder="나이를 입력하세요"
              min="1"
              max="100"
              style={{
                width: '100%',
                padding: '12px 16px',
                border: '1px solid #444',
                borderRadius: 8,
                fontSize: 16,
                background: '#333',
                color: '#fff',
                boxSizing: 'border-box'
              }}
            />
          </div>

          <div style={{ marginBottom: 20 }}>
            <label htmlFor="job" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>직업</label>
            <input
              type="text"
              id="job"
              name="job"
              value={formData.job}
              onChange={handleInputChange}
              placeholder="직업을 입력하세요"
              style={{
                width: '100%',
                padding: '12px 16px',
                border: '1px solid #444',
                borderRadius: 8,
                fontSize: 16,
                background: '#333',
                color: '#fff',
                boxSizing: 'border-box'
              }}
            />
          </div>

          <div style={{ marginBottom: 20 }}>
            <label htmlFor="info" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>기본 정보</label>
            <textarea
              id="info"
              name="info"
              value={formData.info}
              onChange={handleInputChange}
              placeholder="기본 정보를 입력하세요"
              rows={3}
              style={{
                width: '100%',
                padding: '12px 16px',
                border: '1px solid #444',
                borderRadius: 8,
                fontSize: 16,
                background: '#333',
                color: '#fff',
                boxSizing: 'border-box',
                resize: 'vertical',
                minHeight: 80
              }}
            />
          </div>

          <div style={{ marginBottom: 20 }}>
            <label htmlFor="habit" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>습관적인 말과 행동</label>
            <textarea
              id="habit"
              name="habit"
              value={formData.habit}
              onChange={handleInputChange}
              placeholder="습관적인 말과 행동을 입력하세요"
              rows={3}
              style={{
                width: '100%',
                padding: '12px 16px',
                border: '1px solid #444',
                borderRadius: 8,
                fontSize: 16,
                background: '#333',
                color: '#fff',
                boxSizing: 'border-box',
                resize: 'vertical',
                minHeight: 80
              }}
            />
          </div>

          <div style={{
            display: 'flex',
            gap: 12,
            marginTop: 32,
            paddingTop: 20,
            borderTop: '1px solid #333'
          }}>
            <button type="button" onClick={onClose} style={{
              flex: 1,
              padding: '12px 24px',
              borderRadius: 8,
              fontSize: 16,
              fontWeight: 500,
              cursor: 'pointer',
              border: '1px solid #444',
              background: '#333',
              color: '#fff'
            }}>
              취소
            </button>
            <button type="submit" style={{
              flex: 1,
              padding: '12px 24px',
              borderRadius: 8,
              fontSize: 16,
              fontWeight: 500,
              cursor: 'pointer',
              border: 'none',
              background: '#ff4081',
              color: '#fff'
            }}>
              저장
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProfileEditModal;
