import React from "react";

const STAGES: { label: string; min: number; }[] = [
  { label: "ÏïÑÎäî ÏÇ¨Ïù¥", min: -20 },
  { label: "ÏπúÍµ¨", min: 0 },
  { label: "Ïç∏", min: 5 },
  { label: "Ïó∞Ïù∏", min: 10 },
  { label: "Í≤∞Ìòº", min: 20 },
];

export default function FavorDetailModal({
  isOpen,
  onClose,
  character,
  user,
  favor,
  days,
  relation = "ÏïÑÎäî ÏÇ¨Ïù¥"
}: {
  isOpen: boolean;
  onClose: () => void;
  character: { name: string; avatar: string };
  user: { name: string; avatar: string };
  favor: number;
  days: number;
  relation?: string;
}) {
  if (!isOpen) return null;
  const currentStage = [...STAGES].reverse().find(s => favor >= s.min) || STAGES[0];

  return (
    <div style={{
      position: "fixed", top: 0, left: 0, right: 0, bottom: 0, zIndex: 3000,
      background: "linear-gradient(180deg,#fbeaff 0%,#fbeaff 100%)"
    }}>
      <button onClick={onClose} style={{ position: "absolute", left: 16, top: 16, background: "none", border: "none", fontSize: 28, color: "#888" }}>&larr;</button>
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginTop: 60 }}>
        <div style={{ display: "flex", alignItems: "center", gap: 32 }}>
          <img src={character.avatar || "/imgdefault.jpg"} alt={character.name} style={{ width: 80, height: 80, borderRadius: "50%", objectFit: "cover", border: "3px solid #fff" }} onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = "/imgdefault.jpg"; }} />
          <span style={{ fontSize: 36, color: "#ffb3d1" }}>‚ù§</span>
          <img src={user.avatar || "/imgdefault.jpg"} alt={user.name} style={{ width: 80, height: 80, borderRadius: "50%", objectFit: "cover", border: "3px solid #fff" }} onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = "/imgdefault.jpg"; }} />
        </div>
        <div style={{ marginTop: 24, fontWeight: 700, fontSize: 18, color: "#888" }}>
          {character.name}Í≥º {user.name}Ïùò Ïù¥ÏïºÍ∏∞
        </div>
        <div style={{ marginTop: 8, fontSize: 22, fontWeight: 700, color: "#444" }}>
          {days}Ïùº ÎèôÏïà Ïù¥ÏïºÍ∏∞Î•º ÏåìÏïÑÏò§Í≥† ÏûàÏñ¥Ïöî
        </div>
        <div style={{ marginTop: 16, color: "#ff4081", fontWeight: 700, fontSize: 18 }}>{relation}</div>
        {/* Ìò∏Í∞êÎèÑ Í≤åÏù¥ÏßÄ */}
        <div style={{ marginTop: 32, width: 320, background: "#fff", borderRadius: 16, padding: 20, boxShadow: "0 2px 8px #eee" }}>
          <div style={{ fontWeight: 700, fontSize: 16, color: "#ff4081", marginBottom: 8 }}>Ìò∏Í∞êÎèÑ</div>
          <div style={{ width: "100%", height: 12, background: "#ffe3ef", borderRadius: 8, marginBottom: 8, position: "relative" }}>
            <div style={{
              width: `${Math.max(0, Math.min(favor, 20)) / 20 * 100}%`,
              height: "100%",
              background: "#ff4081",
              borderRadius: 8,
              transition: "width 0.3s"
            }} />
          </div>
          <div style={{ textAlign: "right", color: "#ff4081", fontWeight: 700 }}>{favor} / 20</div>
          {/* Îã®Í≥ÑÎ≥Ñ ÏïÑÏù¥ÏΩò/ÌÖçÏä§Ìä∏ */}
          <div style={{ display: "flex", justifyContent: "space-between", marginTop: 16 }}>
            {STAGES.map((s, idx) => (
              <div key={s.label} style={{ textAlign: "center", flex: 1, opacity: favor >= s.min ? 1 : 0.4 }}>
                <div style={{
                  width: 32, height: 32, borderRadius: "50%",
                  background: favor >= s.min ? "#ffb3d1" : "#eee",
                  margin: "0 auto", marginBottom: 4, display: "flex", alignItems: "center", justifyContent: "center"
                }}>
                  <span style={{ fontSize: 18 }}>{favor >= s.min ? "‚ù§" : "üîí"}</span>
                </div>
                <div style={{ fontSize: 13, color: favor >= s.min ? "#ff4081" : "#bbb" }}>{s.label}</div>
              </div>
            ))}
          </div>
        </div>
        {/* Ïπ¥ÎìúÌï® Î≤ÑÌäº */}
        <button style={{
          marginTop: 32, background: "#fff", color: "#ff4081", border: "1.5px solid #ff4081",
          borderRadius: 16, padding: "12px 32px", fontWeight: 700, fontSize: 18, boxShadow: "0 2px 8px #eee"
        }}>Ïπ¥ÎìúÌï®</button>
      </div>
    </div>
  );
}
