import React from 'react';

interface ProfileDetailModalProps {
  isOpen: boolean;
  onClose: () => void;
  profile: {
    id: string;
    name: string;
    avatar: string;
    gender?: string;
    age?: string;
    job?: string;
    info?: string;
    habit?: string;
  };
}

export default function ProfileDetailModal({ isOpen, onClose, profile }: ProfileDetailModalProps) {
  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: 20,
        padding: 24,
        width: '90%',
        maxWidth: 400,
        position: 'relative'
      }}>
        <button
          onClick={onClose}
          style={{
            position: 'absolute',
            right: 16,
            top: 16,
            background: 'none',
            border: 'none',
            fontSize: 24,
            cursor: 'pointer',
            color: '#666'
          }}
        >
          ×
        </button>

        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <img
            src={profile.avatar || "/imgdefault.jpg"}
            alt={profile.name}
            style={{
              width: 100,
              height: 100,
              borderRadius: '50%',
              objectFit: 'cover',
              marginBottom: 16,
              border: '2px solid #eee'
            }}
          />
          <div style={{ fontWeight: 700, fontSize: 24, marginBottom: 8 }}>{profile.name}</div>
          {(profile.gender || profile.age || profile.job) && (
            <div style={{ color: '#888', fontSize: 16, marginBottom: 8 }}>
              {[profile.gender, profile.age, profile.job].filter(Boolean).join(' · ')}
            </div>
          )}
          {profile.info && (
            <div style={{ color: '#222', fontSize: 16, marginBottom: 16, textAlign: 'center' }}>
              {profile.info}
            </div>
          )}
          {profile.habit && (
            <div style={{ color: '#888', fontSize: 15, marginBottom: 16 }}>
              습관: {profile.habit}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
