import React, { useState, useEffect } from 'react';

interface ProfileEditModalProps {
  isOpen: boolean;
  onClose: () => void;
  profileData: {
    id: string;
    name: string;
    gender: string;
    age: number;
    job: string;
    info: string;
    habit: string;
    avatar: string;
  };
  onSave: (updatedProfile: any) => void;
}

const ProfileEditModal: React.FC<ProfileEditModalProps> = ({
  isOpen,
  onClose,
  profileData,
  onSave
}) => {
  const [formData, setFormData] = useState({
    name: '',
    gender: '',
    age: '',
    job: '',
    info: '',
    habit: '',
    avatar: ''
  });
  const fileInputRef = React.useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (isOpen) {
      setFormData({
        name: profileData.name || '',
        gender: profileData.gender || '',
        age: profileData.age?.toString() || '',
        job: profileData.job || '',
        info: profileData.info || '',
        habit: profileData.habit || '',
        avatar: profileData.avatar || '/imgdefault.jpg'
      });
    }
  }, [isOpen]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleProfileImgClick = () => fileInputRef.current?.click();
  const handleProfileImgChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        setFormData(prev => ({ ...prev, avatar: ev.target?.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name.trim()) {
      alert('이름은 필수 입력 항목입니다.');
      return;
    }

    try {
      const updatedProfile = {
        ...profileData,
        name: formData.name,
        gender: formData.gender,
        age: formData.age ? parseInt(formData.age) : undefined,
        job: formData.job,
        info: formData.info,
        habit: formData.habit,
        avatar: formData.avatar
      };

      await onSave(updatedProfile);
      onClose();
    } catch (error) {
      console.error('프로필 수정 중 오류:', error);
      alert('프로필 수정 중 오류가 발생했습니다.');
    }
  };

  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }} onClick={onClose}>
      <div style={{
        background: 'var(--color-card)',
        borderRadius: 16,
        width: '90%',
        maxWidth: 500,
        maxHeight: '90vh',
        overflowY: 'auto'
      }} onClick={(e) => e.stopPropagation()}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '20px 24px',
          borderBottom: '1px solid #333'
        }}>
          <h2 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 600, color: '#fff' }}>프로필 수정</h2>
          <button style={{
            background: 'none',
            border: 'none',
            fontSize: 24,
            cursor: 'pointer',
            color: '#6b7280',
            padding: 0,
            width: 32,
            height: 32,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            borderRadius: 8
          }} onClick={onClose}>×</button>
        </div>

        <form onSubmit={handleSubmit} style={{ padding: 24 }}>
          <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
            <div style={{ position: 'relative' }}>
              <img
                src={formData.avatar || '/imgdefault.jpg'}
                alt="프로필"
                style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', border: '3px solid #fff', background: '#eee' }}
                onError={e => {
                  if (!e.currentTarget.src.endsWith('/imgdefault.jpg')) {
                    e.currentTarget.onerror = null;
                    e.currentTarget.src = '/imgdefault.jpg';
                  }
                }}
              />
              <button
                type="button"
                onClick={handleProfileImgClick}
                style={{
                  position: 'absolute',
                  right: 0,
                  bottom: 0,
                  background: '#ff4081',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '50%',
                  width: 32,
                  height: 32,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: 18,
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
                }}
                aria-label="프로필 사진 변경"
              >
                ✏️
              </button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                style={{ display: 'none' }}
                onChange={handleProfileImgChange}
              />
            </div>
          </div>

          <div style={{ marginBottom: 20 }}>
            <label htmlFor="name" style={{ display: 'block', marginBottom: 8, fontWeight: 500, color: '#fff', fontSize: 14 }}>이름 *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
