// src/hooks/useOnboarding.ts
import { useCallback, useState } from "react";

export function useOnboarding() {
  const [mood, setMood] = useState<string | null>(
    () => localStorage.getItem("user_mood") || null
  );

  const selectMood = useCallback((value: string) => {
    setMood(value);
    localStorage.setItem("user_mood", value);
  }, []);

  return { mood, selectMood };
}
