// src/pages/OnboardingPage.tsx
import React from "react";
import { useNavigate } from "react-router-dom";
import MoodCard from "../components/MoodCard";
import { useOnboarding } from "../hooks/useOnboarding";

const MOODS = ["외로움", "설렘", "위로", "힐링", "자극"];

export default function OnboardingPage() {
  const navigate = useNavigate();
  const { mood, selectMood } = useOnboarding();

  return (
    <div className="h-screen bg-pink-50 flex flex-col items-center justify-center px-4">
      <h2 className="text-2xl font-semibold mb-8">당신의 지금 기분은?</h2>
      <div className="grid grid-cols-2 gap-4 mb-8">
        {MOODS.map((m) => (
          <MoodCard
            key={m}
            label={m}
            selected={mood === m}
            onClick={selectMood}
          />
        ))}
      </div>
      <button
        onClick={() => mood && navigate("/intro")}
        disabled={!mood}
        className={`w-full max-w-xs px-6 py-3 rounded-lg text-white transition ${
          mood ? "bg-pink-500 hover:bg-pink-600" : "bg-pink-200 cursor-not-allowed"
        }`}
      >
        다음으로
      </button>
    </div>
  );
}
