import { useCallback, useEffect, useState } from "react";

export interface Msg {
  sender: "user" | "ai";
  text: string;
  avatar?: string;
}

export function useChat(characterId: string, userId: string) {
  const [messages, setMessages] = useState<Msg[]>([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [favor, setFavor] = useState(0);

  // 메시지 불러오기
  useEffect(() => {
    if (!characterId || !userId) return;
    fetch(`/api/chat?userId=${userId}&characterId=${characterId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) {
          setMessages(
            data.messages.map((msg: any) => ({
              sender: msg.sender,
              text: msg.message,
              avatar: msg.sender === "ai" ? `/avatars/${characterId}.jpg` : undefined,
            }))
          );
          if (typeof data.favor === "number") setFavor(data.favor);
        }
      });
  }, [characterId, userId]);

  const sendMessage = useCallback(async (msg: string) => {
    if (!msg.trim()) return;

    setLoading(true);
    setMessages((prev) => [...prev, { sender: "user", text: msg }]);
    const userMsg = msg;
    setInput("");

    try {
      const history = messages.slice(-9).concat([{ sender: "user", text: msg }]);
      const requestBody = {
        userId,
        characterId,
        message: userMsg,
        sender: "user",
        history: history.map(m => ({ ...m, message: m.text }))
      };
      console.log('Sending request:', requestBody);

      const response = await fetch("/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
    });
      const data = await response.json();
      console.log('Received response:', data);

      if (data.ok) {
        if (data.aiText) {
    setMessages((prev) => [
      ...prev,
      {
        sender: "ai",
              text: data.aiText,
        avatar: `/avatars/${characterId}.jpg`,
      },
    ]);
        }
        if (typeof data.favorDelta === "number" && !isNaN(data.favorDelta)) {
          setFavor((prev) => prev + data.favorDelta);
        }
      } else {
        console.error("Failed to send message:", data.error);
      }
    } catch (error) {
      console.error("Error sending message:", error);
    } finally {
      setLoading(false);
    }
  }, [input, characterId, userId, messages]);

  return { messages, input, setInput, sendMessage, setMessages, loading, favor };
}
