import React, { useEffect, useState, useRef } from "react";
import BottomNav from "../components/BottomNav";
import { useNavigate } from "react-router-dom";
import { FiSettings } from "react-icons/fi";
import ProfileEditModal from "../components/ProfileEditModal";

interface Character {
  id: number;
  profileImg: string;
  name: string;
}

interface Persona {
  id: string;
  userId?: string;
  name: string;
  avatar: string;
  gender?: string;
  age?: string;
  job?: string;
  info?: string;
  habit?: string;
}

// 프로필 이미지 경로 상수로 지정
const DEFAULT_PROFILE_IMG = "/imgdefault.jpg";

export default function MyPage() {
  const navigate = useNavigate();
  const [characters, setCharacters] = useState<Character[]>([]);
  const [personas, setPersonas] = useState<Persona[]>([]);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showUserProfileEditModal, setShowUserProfileEditModal] = useState(false);
  const [showProfileEditModal, setShowProfileEditModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<Persona | null>(null);
  const userId = "user_74127"; // 실제 로그인 계정 id로 대체
  const [userProfile, setUserProfile] = useState<{ name: string; avatar: string }>({ name: "user_74127", avatar: "/imgdefault.jpg" });
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [showProfileCreateModal, setShowProfileCreateModal] = useState(false);

  useEffect(() => {
    // 멀티프로필 목록 불러오기
    fetch(`/api/persona?userId=${userId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok && data.personas.length > 0) {
          // 기본 프로필(userId와 같은 id를 가진 것) 찾기
          const defaultPersona = data.personas.find((p: Persona) => p.id === userId);
          if (defaultPersona) {
            setUserProfile({ name: defaultPersona.name, avatar: defaultPersona.avatar });
          }
          setPersonas(data.personas);
        }
      });

    // 내 캐릭터 목록 불러오기
    fetch(`/api/character?userId=${userId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setCharacters(data.characters);
      });

    // 사용자 프로필 정보 불러오기 (localStorage에서)
    const savedUserProfile = localStorage.getItem('userProfile');
    if (savedUserProfile) {
      const profile = JSON.parse(savedUserProfile);
      setUserProfile({ name: profile.name || "user_74127", avatar: profile.avatar || "/imgdefault.jpg" });
    }
  }, [userId]);

  const handleUserProfileImageClick = () => {
    fileInputRef.current?.click();
  };

  const handleUserProfileImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        const newProfileImg = ev.target?.result as string;
        setUserProfile(prev => ({ ...prev, avatar: newProfileImg }));
        // localStorage에 저장
        const userProfileToSave = {
          name: userProfile.name,
          avatar: newProfileImg
        };
        localStorage.setItem('userProfile', JSON.stringify(userProfileToSave));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUserNameSave = () => {
    // localStorage에 저장
    const userProfileToSave = {
      name: userProfile.name,
      avatar: userProfile.avatar
    };
    localStorage.setItem('userProfile', JSON.stringify(userProfileToSave));
    setShowUserProfileEditModal(false);
  };

  const handleProfileEdit = (profile: Persona) => {
    setSelectedProfile(profile);
    setShowProfileEditModal(true);
  };

  // 멀티프로필 목록 최신화 함수
  const fetchPersonas = async () => {
    const res = await fetch(`/api/persona?userId=${userId}`);
    const data = await res.json();
    if (data.ok && data.personas.length > 0) {
      setPersonas(data.personas);
      // 기본 프로필도 갱신
      const defaultPersona = data.personas.find((p: Persona) => p.id === userId);
      if (defaultPersona) {
        setUserProfile({ name: defaultPersona.name, avatar: defaultPersona.avatar });
      }
    }
  };

  const handleProfileSave = async (updatedProfile: Persona) => {
    try {
      const response = await fetch(`/api/persona/${updatedProfile.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: updatedProfile.name,
          gender: updatedProfile.gender,
          age: updatedProfile.age,
          job: updatedProfile.job,
          info: updatedProfile.info,
          habit: updatedProfile.habit,
          avatar: updatedProfile.avatar
        }),
      });

      if (response.ok) {
        setShowProfileEditModal(false);
        await fetchPersonas(); // 최신 목록으로 갱신
        alert('프로필이 성공적으로 수정되었습니다.');
      } else {
        throw new Error('프로필 수정에 실패했습니다.');
      }
    } catch (error) {
      console.error('프로필 수정 오류:', error);
      alert('프로필 수정 중 오류가 발생했습니다.');
    }
  };

  return (
    <div style={{ background: "var(--color-bg)", minHeight: "100vh", paddingBottom: 80 }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: "24px 20px 0 20px" }}>
        <span style={{ fontWeight: 700, fontSize: 24 }}>MY</span>
        <button
          onClick={() => setShowSettingsModal(true)}
          style={{ background: 'none', border: 'none', cursor: 'pointer', padding: 4 }}
          aria-label="설정 및 로그아웃"
        >
          <FiSettings size={26} color="#fff" />
        </button>
      </div>
      <div style={{
        background: "var(--color-card)",
        borderRadius: 20,
        margin: 20,
        padding: 20,
        boxShadow: "0 2px 8px rgba(0,0,0,0.03)",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between"
      }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={userProfile.avatar || DEFAULT_PROFILE_IMG}
            alt="프로필"
            style={{ width: 60, height: 60, borderRadius: "50%", marginRight: 16, objectFit: "cover", cursor: "pointer" }}
            onClick={() => setShowUserProfileEditModal(true)}
            onError={e => {
              if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                e.currentTarget.onerror = null;
                e.currentTarget.src = "/imgdefault.jpg";
              }
            }}
          />
          <div>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{userProfile.name}</div>
            <div style={{ color: "var(--color-subtext)", fontSize: 15, marginTop: 4 }}>보유 잼</div>
          </div>
        </div>
        <div style={{ textAlign: "right" }}>
          <button 
            onClick={() => setShowUserProfileEditModal(true)}
            style={{
              background: "#f5f5f5",
              border: "none",
              borderRadius: 16,
              padding: "8px 18px",
              fontWeight: 600,
              fontSize: 15,
              marginBottom: 16,
              cursor: "pointer"
            }}
          >
            내 프로필
          </button>
          <div style={{ fontWeight: 700, fontSize: 18, color: "#ff4081" }}>35 <span role="img" aria-label="잼">🍓</span></div>
        </div>
      </div>
      {/* 멀티프로필 섹션 - MY와 내 캐릭터 사이 */}
      <div style={{ marginTop: 16, padding: "0 20px" }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 8 }}>
          <div style={{ fontWeight: 700, fontSize: 16 }}>멀티프로필</div>
          <button
            onClick={() => setShowProfileCreateModal(true)}
            disabled={personas.length >= 10}
            style={{
              background: 'none',
              border: 'none',
              color: personas.length >= 10 ? '#888' : '#bbb',
              fontWeight: 600,
              fontSize: 16,
              cursor: personas.length >= 10 ? 'not-allowed' : 'pointer',
              display: 'flex',
              alignItems: 'center'
            }}
            title={personas.length >= 10 ? '최대 10개까지 생성할 수 있습니다.' : ''}
          >
            <span style={{ fontSize: 22, marginRight: 6 }}>+</span> 멀티프로필 만들기 ({personas.length}/10)
          </button>
        </div>
        {/* 기본프로필(삭제X) */}
        {personas.filter(p => p.id === userId).map(p => (
          <div key={p.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card-alt)", borderRadius: 12, padding: 10, marginBottom: 10 }}>
            <img
              src={userProfile.avatar || DEFAULT_PROFILE_IMG}
              alt={userProfile.name}
              style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
              onError={e => {
                if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                  e.currentTarget.onerror = null;
                  e.currentTarget.src = "/imgdefault.jpg";
                }
              }}
            />
            <span style={{ fontWeight: 600, fontSize: 16 }}>{userProfile.name} <span style={{ color: "#ff4081", fontSize: 13, marginLeft: 6 }}>(기본)</span></span>
          </div>
        ))}
        {/* 나머지 멀티프로필(수정O, 삭제O) */}
        {personas.filter(p => p.id !== userId).map(p => (
          <div key={p.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card-alt)", borderRadius: 12, padding: 10, marginBottom: 10 }}>
            <img
              src={p.avatar || DEFAULT_PROFILE_IMG}
              alt={p.name}
              style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
              onError={e => {
                if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                  e.currentTarget.onerror = null;
                  e.currentTarget.src = "/imgdefault.jpg";
                }
              }}
            />
            <span style={{ fontWeight: 600, fontSize: 16 }}>{p.name}</span>
            <button 
              style={{ marginLeft: "auto", marginRight: 12, color: "#4CAF50", background: "none", border: "none", fontSize: 16, cursor: "pointer" }} 
              onClick={() => handleProfileEdit(p)}
            >
              수정
            </button>
            <button style={{ color: "#ff4081", background: "none", border: "none", fontSize: 16, cursor: "pointer" }} onClick={async () => {
              if (window.confirm("정말로 삭제하시겠습니까?")) {
                await fetch(`/api/persona/${p.id}`, { method: "DELETE" });
                setPersonas(prev => prev.filter(x => x.id !== p.id));
              }
            }}>삭제</button>
          </div>
        ))}
      </div>
      <div style={{ padding: "0 20px", fontWeight: 700, fontSize: 18, marginTop: 24 }}>내 캐릭터</div>
      <div style={{ padding: "0 20px" }}>
        {characters.length === 0 ? (
          <div style={{ color: "var(--color-subtext)" }}>아직 만든 캐릭터가 없습니다.</div>
        ) : (
          characters.map(char => (
            <div key={char.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card)", borderRadius: 12, padding: 12, marginBottom: 12 }}>
              <img
                src={char.profileImg || DEFAULT_PROFILE_IMG}
                alt={char.name}
                style={{ width: 48, height: 48, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
                onError={e => {
                  if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                    e.currentTarget.onerror = null;
                    e.currentTarget.src = "/imgdefault.jpg";
                  }
                }}
              />
              <span style={{ fontWeight: 600, fontSize: 16 }}>{char.name}</span>
              <button style={{ marginLeft: "auto", color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={() => navigate(`/character/${char.id}`)}>수정</button>
            </div>
          ))
        )}
      </div>
      <BottomNav />

      {/* 유저 프로필 수정 모달 */}
      {showUserProfileEditModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 2000
        }}>
          <div style={{ background: 'var(--color-card)', borderRadius: 20, padding: 32, width: '90%', maxWidth: 400, position: 'relative', boxShadow: '0 2px 16px rgba(0,0,0,0.18)' }}>
            <button
              onClick={() => setShowUserProfileEditModal(false)}
              style={{ position: 'absolute', right: 16, top: 16, background: 'none', border: 'none', fontSize: 24, cursor: 'pointer', color: '#fff' }}
              aria-label="닫기"
            >×</button>
            <div style={{ fontWeight: 700, fontSize: 22, color: '#fff', marginBottom: 32, textAlign: 'center' }}>프로필 수정</div>

            {/* 프로필 이미지 */}
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
              <div style={{ position: 'relative' }}>
                <img
                  src={userProfile.avatar || DEFAULT_PROFILE_IMG}
                  alt="프로필"
                  style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', border: '3px solid #fff' }}
                  onError={e => {
                    if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                      e.currentTarget.onerror = null;
                      e.currentTarget.src = "/imgdefault.jpg";
                    }
                  }}
                />
                <button
                  onClick={handleUserProfileImageClick}
                  style={{
                    position: 'absolute',
                    right: 0,
                    bottom: 0,
                    background: '#ff4081',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '50%',
                    width: 32,
                    height: 32,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: 18,
                    cursor: 'pointer',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
                  }}
                  aria-label="프로필 사진 변경"
                >
                  ✏️
                </button>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleUserProfileImageChange}
                />
              </div>
            </div>

            {/* 이름 입력 */}
            <div style={{ marginBottom: 24 }}>
              <label style={{ display: 'block', fontWeight: 600, fontSize: 16, color: '#fff', marginBottom: 8 }}>이름</label>
              <input
                type="text"
                value={userProfile.name}
                onChange={(e) => setUserProfile({ ...userProfile, name: e.target.value })}
                style={{
                  width: '100%',
                  padding: '12px 16px',
                  borderRadius: 12,
                  border: '1px solid #444',
                  background: '#333',
                  color: '#fff',
                  fontSize: 16,
                  outline: 'none'
                }}
                placeholder="이름을 입력하세요"
              />
            </div>

            {/* 저장 버튼 */}
            <button
              onClick={handleUserNameSave}
              style={{
                width: '100%',
                background: '#ff4081',
                color: '#fff',
                border: 'none',
                borderRadius: 12,
                padding: 16,
                fontWeight: 700,
                fontSize: 18,
                cursor: 'pointer'
              }}
            >
              저장
            </button>
          </div>
        </div>
      )}

      {/* 설정 모달 */}
      {showSettingsModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 2000
        }}>
          <div style={{ background: '#222', borderRadius: 20, padding: 32, width: '90%', maxWidth: 340, position: 'relative', boxShadow: '0 2px 16px rgba(0,0,0,0.18)', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <button
              onClick={() => setShowSettingsModal(false)}
              style={{ position: 'absolute', right: 16, top: 16, background: 'none', border: 'none', fontSize: 24, cursor: 'pointer', color: '#fff' }}
              aria-label="닫기"
            >×</button>
            <div style={{ fontWeight: 700, fontSize: 22, color: '#fff', marginBottom: 32 }}>설정</div>
            <button
              onClick={() => {
                localStorage.clear();
                sessionStorage.clear();
                // 구글 계정까지 완전 로그아웃 후 /login으로 이동
                window.location.href = "https://accounts.google.com/Logout?continue=https://appengine.google.com/_ah/logout?continue=" + encodeURIComponent(window.location.origin + "/login");
              }}
              style={{ width: '100%', background: '#ff4081', color: '#fff', border: 'none', borderRadius: 12, padding: 16, fontWeight: 700, fontSize: 18, cursor: 'pointer', marginBottom: 8 }}
            >로그아웃</button>
          </div>
        </div>
      )}

      {/* 프로필 수정 모달 */}
      {showProfileEditModal && selectedProfile && (
        <ProfileEditModal
          isOpen={showProfileEditModal}
          onClose={async () => {
            setShowProfileEditModal(false);
            setSelectedProfile(null);
            await fetchPersonas(); // 모달 닫을 때도 최신화
          }}
          profileData={{
            id: selectedProfile.id,
            name: selectedProfile.name,
            gender: selectedProfile.gender || '',
            age: parseInt(selectedProfile.age || '0') || 0,
            job: selectedProfile.job || '',
            info: selectedProfile.info || '',
            habit: selectedProfile.habit || '',
            avatar: selectedProfile.avatar || '/imgdefault.jpg'
          }}
          onSave={handleProfileSave}
        />
      )}
    </div>
  );
}
