import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { useChat } from "../hooks/useChat";
import MessageBubble from "../components/MessageBubble";
import ProfileDetailModal from "../components/ProfileDetailModal";
import FavorDetailModal from "../components/FavorDetailModal";
import Toast from "../components/Toast";

interface Character {
  id: number;
  name: string;
  profileImg: string;
  age?: number;
  job?: string;
  info?: string;
  habit?: string;
  firstScene?: string;
  firstMessage?: string;
}

// ì¡°ì‚¬ ìë™ íŒë³„ í•¨ìˆ˜: ë°›ì¹¨ ìˆìœ¼ë©´ 'ê³¼', ì—†ìœ¼ë©´ 'ì™€'
function getPostposition(name: string) {
  if (!name) return 'ì™€';
  const lastChar = name[name.length - 1];
  const code = lastChar.charCodeAt(0);
  // í•œê¸€ ìœ ë‹ˆì½”ë“œ ë²”ìœ„ ë‚´ì—ì„œ ë°›ì¹¨ ì—¬ë¶€ íŒë³„
  if (0xac00 <= code && code <= 0xd7a3) {
    const jong = (code - 0xac00) % 28;
    return jong === 0 ? 'ì™€' : 'ê³¼';
  }
  // í•œê¸€ì´ ì•„ë‹ˆë©´ ê¸°ë³¸ 'ì™€'
  return 'ì™€';
}

export default function ChatPage() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const personaId = params.get("persona") || "user_74127";
  const { messages, sendMessage, loading, favor } = useChat(id ?? "", personaId);
  const [input, setInput] = useState("");
  const [character, setCharacter] = useState<Character | null>(null);
  const [persona, setPersona] = useState<{ name: string; avatar: string }>({ name: "ë‚˜", avatar: "/avatars/default-profile.png" });
  const [days, setDays] = useState(1);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<{
    id: string;
    name: string;
    avatar: string;
    gender?: string;
    age?: string;
    job?: string;
    info?: string;
    habit?: string;
  } | null>(null);
  const [showFavorModal, setShowFavorModal] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [toast, setToast] = useState<{ message: string; type: "success" | "error" } | null>(null);
  const favorRef = useRef(favor);
  const [showNotice, setShowNotice] = useState(true);

  // ë‹¨ê³„ë³„ ì •ë³´
  const STAGES = [
    { label: "ì•„ëŠ”ì‚¬ì´", min: 0, desc: "ìƒˆë¡œìš´ ì¸ì—°ì„ ë§ºì„ ì¤€ë¹„ê°€ ë˜ì—ˆë‚˜ìš”?", icon: "ğŸ¤" },
    { label: "ì¹œêµ¬", min: 20, desc: "ì„œë¡œ ì›ƒê³  ë– ë“¤ë©° ì¼ìƒì„ ê³µìœ í•´ìš”", icon: "ğŸ˜Š" },
    { label: "ì¸", min: 50, desc: "ê°ì •ì´ ì‹¹íŠ¸ë©° ì„¤ë ˜ì„ ëŠê»´ìš”", icon: "ğŸ’“" },
    { label: "ì—°ì¸", min: 400, desc: "ê°™ì´ ì‹œê°„ì„ ë³´ë‚´ë©° ë‘˜ë§Œì˜ ëŸ¬ë¸ŒìŠ¤í† ë¦¬ë¥¼ ë§Œë“¤ì–´ê°€ìš”", icon: "ğŸ’‘" },
    { label: "ê²°í˜¼", min: 4000, desc: "ì˜¤ëœ ì‹ ë¢°ì™€ í—Œì‹ ìœ¼ë¡œ ë‹¨ë‹¨í•˜ê²Œ ìŒ“ì•„ì˜¨ ê¹Šì€ ì‚¬ë‘ì„ ì¶•í•˜í•´ìš”", icon: "ğŸ’" },
  ];
  const currentStageIdx = [...STAGES].reverse().findIndex(s => favor >= s.min);
  const stageIdx = currentStageIdx === -1 ? 0 : STAGES.length - 1 - currentStageIdx;
  const [selectedStageIdx, setSelectedStageIdx] = useState(stageIdx);
  useEffect(() => { setSelectedStageIdx(stageIdx); }, [stageIdx]);

  useEffect(() => {
    if (!id) return;
    fetch(`/api/character/${id}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setCharacter(data.character);
      });
  }, [id]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    if (personaId) {
      fetch(`/api/persona/${personaId}`)
        .then(res => res.json())
        .then(data => {
          if (data.ok) setPersona({ name: data.persona.name, avatar: data.persona.avatar });
        });
    }
  }, [personaId]);

  useEffect(() => {
    if (!id || !personaId) return;
    fetch(`/api/chat/first-date?userId=${personaId}&characterId=${id}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok && data.firstDate) {
          const start = new Date(data.firstDate);
          const now = new Date();
          const diff = Math.floor((now.getTime() - start.getTime()) / (1000 * 60 * 60 * 24)) + 1;
          setDays(diff);
        }
      });
  }, [id, personaId]);

  useEffect(() => { favorRef.current = favor; }, [favor]);

  useEffect(() => {
    if (!character || !persona) return;
    if (favorRef.current === undefined) return;
    const diff = favor - favorRef.current;
    if (diff === 0) return;
    if (diff > 0) {
      setToast({
        message: `ì¶•í•˜í•©ë‹ˆë‹¤! ${character.name}${getPostposition(character.name)} ${persona.name}ë‹˜ì˜ í˜¸ê°ë„ê°€ ${diff}ë§Œí¼ ì¦ê°€ í–ˆìŠµë‹ˆë‹¤!`,
        type: "success"
      });
    } else if (diff < 0) {
      setToast({
        message: `ì•„ì‰¬ì›Œìš” ã…  ${character.name}${getPostposition(character.name)} ${persona.name}ë‹˜ì˜ í˜¸ê°ë„ê°€ ${-diff}ë§Œí¼ ê°ì†Œ í–ˆìŠµë‹ˆë‹¤ :(`,
        type: "error"
      });
    }
  }, [favor]);

  useEffect(() => {
    const timer = setTimeout(() => setShowNotice(false), 3000);
    return () => clearTimeout(timer);
  }, []);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;
    sendMessage(input);
    setInput("");
  };

  const handleProfileClick = (profile: {
    id: string;
    name: string;
    avatar: string;
    gender?: string;
    age?: string;
    job?: string;
    info?: string;
    habit?: string;
  }) => {
    setSelectedProfile(profile);
    setShowProfileModal(true);
  };

  // í”„ë¡œí•„ ëª¨ë‹¬ì´ ì—´ë¦´ ë•Œ characterê°€ ìˆìœ¼ë©´ í•­ìƒ characterë¥¼ ì„ íƒ
  useEffect(() => {
    if (showProfileModal) {
      if (selectedProfile && selectedProfile.id !== character?.id?.toString()) {
        // ì´ë¯¸ ì„ íƒëœ í”„ë¡œí•„ì´ ìºë¦­í„°ê°€ ì•„ë‹ˆë©´ ê·¸ëŒ€ë¡œ ë‘”ë‹¤(í˜ë¥´ì†Œë‚˜ ë“±)
        return;
      }
      if (character) {
        setSelectedProfile({
          id: character.id.toString(),
          name: character.name,
          avatar: character.profileImg,
          age: character.age?.toString(),
          job: character.job,
          info: character.info,
          habit: character.habit
        });
      }
    }
  }, [showProfileModal, character, selectedProfile]);

  if (!character) return <div>ë¡œë”© ì¤‘...</div>;

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh", background: "var(--color-bg)" }}>
      {/* í—¤ë” */}
      <div style={{ background: "var(--color-card)", padding: "12px 20px", display: "flex", alignItems: "center", justifyContent: "space-between", borderBottom: "1px solid var(--color-border)", position: "sticky", top: 0, zIndex: 10 }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <button onClick={() => navigate(-1)} style={{ background: "none", border: "none", fontSize: 24, marginRight: 12, cursor: "pointer", color: "#fff" }}>&larr;</button>
          <img
            src={character.profileImg}
            alt={character.name}
            style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, cursor: "pointer" }}
            onClick={() => handleProfileClick({
              id: character.id.toString(),
              name: character.name,
              avatar: character.profileImg,
              age: character.age?.toString(),
              job: character.job,
              info: character.info,
              habit: character.habit
            })}
          />
          <div>
            <div style={{ fontWeight: 700, fontSize: 18 }}>{character.name}</div>
            <div style={{ color: "#888", fontSize: 14 }}>{character.age ? `${character.age}ì‚´` : "ë‚˜ì´ ë¹„ê³µê°œ"} Â· {character.job || "ì§ì—… ë¹„ê³µê°œ"}</div>
          </div>
        </div>
        <div style={{ display: "flex", gap: 16 }}>
          <span style={{ fontSize: 20, cursor: "pointer" }}>ğŸ”</span>
          <span style={{ fontSize: 20, cursor: "pointer" }}>â‹®</span>
        </div>
      </div>
      {/* íƒ­/ìƒíƒœ */}
      <div style={{ background: "var(--color-card)", display: "flex", alignItems: "center", borderBottom: "1.5px solid var(--color-point)", padding: "0 0 0 0", position: "sticky", top: 56, zIndex: 9 }}>
        <div style={{ flex: 1, textAlign: "center", padding: 12, fontWeight: 700, color: "#ff4081", borderBottom: "2.5px solid #ff4081" }}>ì•„ëŠ” ì‚¬ì´</div>
        <div style={{ flex: 1, textAlign: "center", padding: 12, fontWeight: 700, color: "#bbb" }}>ì¹œêµ¬</div>
        <div style={{ flex: 1, textAlign: "center", padding: 12, fontWeight: 700, color: "#bbb" }}></div>
        <button
          style={{ flex: 1.5, textAlign: "center", fontWeight: 700, color: "#ff4081", fontSize: 15, background: "none", border: "none", cursor: "pointer", padding: 0 }}
          onClick={() => setShowFavorModal(true)}
        >
          í˜¸ê°ë„: <span style={{ color: '#ff4081', fontWeight: 700 }}>{favor}ì </span>
        </button>
      </div>
      {/* ë‹¨ê³„ë³„ ìŠ¤í…ë°” */}
      <div style={{ background: "var(--color-card)", display: "flex", alignItems: "center", justifyContent: "center", gap: 0, padding: "0 0 0 0", borderBottom: "1.5px solid var(--color-point)", position: "sticky", top: 56, zIndex: 9 }}>
        {STAGES.map((s, idx) => (
          <div
            key={s.label}
            onClick={() => setSelectedStageIdx(idx)}
            style={{
              flex: 1,
              textAlign: "center",
              padding: "12px 0 8px 0",
              fontWeight: 700,
              color: idx === stageIdx ? "#ff4081" : idx < stageIdx ? "#ffb3d1" : "#bbb",
              borderBottom: idx === stageIdx ? "2.5px solid #ff4081" : "none",
              cursor: "pointer",
              background: "none",
              fontSize: 16,
              transition: "color 0.2s, border 0.2s"
            }}
          >
            <span style={{ fontSize: 20, marginRight: 4 }}>{s.icon}</span>{s.label}
          </div>
        ))}
      </div>
      {/* ë‹¨ê³„ë³„ ì„¤ëª… */}
      <div style={{ background: "#fff0f6", color: "#ff4081", fontSize: 15, textAlign: "center", padding: "10px 0", fontWeight: 600, minHeight: 32 }}>
        {STAGES[selectedStageIdx].desc}
      </div>
      {/* ìœ ì²¨ì´ë¯¸ì§€(í”„ë¡œí•„) UI */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', background: 'var(--color-bg)', padding: '16px 24px 8px 24px', borderBottom: '1px solid #222' }}>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', cursor: 'pointer' }} onClick={() => handleProfileClick({
          id: character.id.toString(),
          name: character.name,
          avatar: character.profileImg,
          age: character.age?.toString(),
          job: character.job,
          info: character.info,
          habit: character.habit
        })}>
          <img src={character.profileImg || '/imgdefault.jpg'} alt={character.name} style={{ width: 56, height: 56, borderRadius: '50%', objectFit: 'cover', border: '2px solid #ff4081', marginBottom: 4 }} onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = '/imgdefault.jpg'; }} />
          <span style={{ fontSize: 13, color: '#fff', fontWeight: 600, marginTop: 2 }}>{character.name}</span>
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', cursor: 'pointer' }} onClick={() => handleProfileClick({
          id: personaId,
          name: persona.name,
          avatar: persona.avatar
        })}>
          <img src={persona.avatar || '/imgdefault.jpg'} alt={persona.name} style={{ width: 56, height: 56, borderRadius: '50%', objectFit: 'cover', border: '2px solid #ff4081', marginBottom: 4 }} onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = '/imgdefault.jpg'; }} />
          <span style={{ fontSize: 13, color: '#fff', fontWeight: 600, marginTop: 2 }}>{persona.name}</span>
        </div>
      </div>
      {/* ê²½ê³  ì•ˆë‚´ */}
      {showNotice && (
        <div style={{ background: "#fff0f6", color: "var(--color-point)", fontSize: 14, padding: 16, margin: "0 0 12px 0", borderRadius: 12, marginTop: 8, marginLeft: 12, marginRight: 12 }}>
          <b>âš ï¸ ì±„íŒ… ì‘ë‹µ ë° ì½˜í…ì¸ ëŠ” AIì— ì˜í•´ ìƒì„±ë˜ì—ˆìœ¼ë©°, ì˜¤ë¥˜ë‚˜ ë¶€ì •í™•í•œ ì •ë³´ë¥¼ í¬í•¨í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.</b> ì´ ë‚´ìš©ì€ ëŸ¬ë¸”ì±—ì˜ ê³µì‹ ì…ì¥ê³¼ëŠ” ë¬´ê´€í•¨ì„ ì•Œë ¤ë“œë¦½ë‹ˆë‹¤. ì‘ë‹µ í’ˆì§ˆì„ ë†’ì´ê¸° ìœ„í•´ ë¶€ì ì ˆí•œ ë©”ì‹œì§€ëŠ” ê¼­ ëˆŒëŸ¬ ì˜¤ë¥˜ë¥¼ ì•Œë ¤ì£¼ì„¸ìš”!
        </div>
      )}
      {/* ìŠ¤í† ë¦¬/ë©”ì‹œì§€ ì˜ì—­ */}
      <div style={{ flex: 1, overflowY: "auto", padding: "0 0 16px 0", display: "flex", flexDirection: "column" }}>
        {/* ì²« ì¥ë©´/ì²«ëŒ€ì‚¬ í‘œì‹œ */}
        {character?.firstScene && (
          <div style={{ color: "#b97cae", fontSize: 16, textAlign: "center", margin: "32px 0 8px 0", whiteSpace: "pre-line" }}>
            <b>ì²« ì¥ë©´</b><br />{character.firstScene}
          </div>
        )}
        {character?.firstMessage && (
          <div style={{ color: "#ff4081", fontSize: 16, textAlign: "center", margin: "8px 0 16px 0", whiteSpace: "pre-line" }}>
            <b>ì²« ëŒ€ì‚¬</b><br />{character.firstMessage}
          </div>
        )}
        {/* ë©”ì‹œì§€ ë¦¬ìŠ¤íŠ¸ */}
        {messages.map((msg, idx) => (
          <div key={idx} style={{ padding: "0 16px", marginBottom: 8 }}>
            <MessageBubble
              message={{
                ...msg,
                avatar: msg.sender === "ai"
                  ? character.profileImg || "/avatars/default-profile.png"
                  : persona.avatar
              }}
              onProfileClick={() => {
                if (msg.sender === "ai") {
                  handleProfileClick({
                    id: character.id.toString(),
                    name: character.name,
                    avatar: character.profileImg,
                    age: character.age?.toString(),
                    job: character.job,
                    info: character.info,
                    habit: character.habit
                  });
                } else if (msg.sender === "user") {
                  handleProfileClick({
                    id: personaId,
                    name: persona.name,
                    avatar: persona.avatar
                  });
                }
              }}
            />
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      {/* ì…ë ¥ ì˜ì—­ */}
      <form onSubmit={handleSubmit} style={{ background: "var(--color-card)", padding: "12px 16px", borderTop: "1px solid var(--color-border)" }}>
        <div style={{ display: "flex", gap: 8 }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
            placeholder="ë©”ì‹œì§€ë¥¼ ì…ë ¥í•˜ì„¸ìš”..."
            style={{
              flex: 1,
              padding: "12px 16px",
              borderRadius: 24,
              border: "1px solid #eee",
              fontSize: 16,
              outline: "none"
            }}
        />
        <button
            type="submit"
            disabled={loading || !input.trim()}
            style={{
              background: loading || !input.trim() ? "#f5f5f5" : "#ff4081",
              color: loading || !input.trim() ? "#bbb" : "#fff",
              border: "none",
              borderRadius: 24,
              padding: "0 24px",
              fontWeight: 700,
              fontSize: 16,
              cursor: loading || !input.trim() ? "not-allowed" : "pointer"
            }}
        >
          ì „ì†¡
        </button>
        </div>
      </form>

      {/* í”„ë¡œí•„ ìƒì„¸ ëª¨ë‹¬ */}
      <ProfileDetailModal
        isOpen={showProfileModal}
        onClose={() => setShowProfileModal(false)}
        profile={selectedProfile || {
          id: "",
          name: "",
          avatar: ""
        }}
      />

      {/* í˜¸ê°ë„ ìƒì„¸ ëª¨ë‹¬ */}
      <FavorDetailModal
        isOpen={showFavorModal}
        onClose={() => setShowFavorModal(false)}
        character={{ name: character.name, avatar: character.profileImg }}
        user={{ name: persona.name, avatar: persona.avatar }}
        favor={favor}
        days={days}
      />

      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
    </div>
  );
}
