export default function CharacterDetailPage() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [character, setCharacter] = useState<Character | null>(null);
  const [showPersonaModal, setShowPersonaModal] = useState(false);
  const [selectedPersona, setSelectedPersona] = useState("user_74127"); // 기본값으로 기본 프로필 ID
  const [showAllTags, setShowAllTags] = useState(false);

  useEffect(() => {
    fetch(`/api/character/${id}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setCharacter(data.character);
      });
  }, [id]);

  // 채팅하기 버튼 클릭 시
  const handleChatClick = () => {
    setShowPersonaModal(true);
  };

  // 채팅 시작
  const handleStartChat = () => {
    setShowPersonaModal(false);
    navigate(`/chat/${id}?persona=${selectedPersona}`);
  };

  if (!character) return <CharacterDetailSkeleton />;

  // 해시태그 더보기 로직
  const TAGS_TO_SHOW = 8;
  const tags = character.selectedTags || [];
  const showTags = showAllTags ? tags : tags.slice(0, TAGS_TO_SHOW);
  const hasMoreTags = tags.length > TAGS_TO_SHOW;

  // 탭 상태
  const [tab, setTab] = useState<'first' | 'story' | 'community'>('story');

  return (
    <div style={{ background: "var(--color-bg)", minHeight: "100vh", paddingBottom: 80 }}>
      {/* 상단 3:4 비율 이미지 */}
      <div style={{ position: "relative", width: "100%", aspectRatio: "3/4", background: `url(${character.profileImg || "/avatars/default-profile.png"}) center/cover no-repeat`, overflow: "hidden" }}>
        {/* 오버레이 */}
        <div style={{ position: "absolute", inset: 0, background: "linear-gradient(180deg,rgba(0,0,0,0.38) 60%,rgba(0,0,0,0.85) 100%)" }} />
        {/* 상단 정보 */}
        <button onClick={() => navigate(-1)} style={{ position: 'absolute', left: 16, top: 16, background: 'none', border: 'none', color: '#fff', fontSize: 28, zIndex: 2 }}>&larr;</button>
        <div style={{ position: "absolute", left: 56, top: 20, display: "flex", alignItems: "center", gap: 10, zIndex: 2 }}>
          <span style={{ background: "#ff5fa2", color: "#fff", fontWeight: 700, fontSize: 13, borderRadius: 8, padding: "4px 10px" }}>{character.category || "카테고리 없음"}</span>
          {/* 조회수, by 정보 등 필요시 추가 */}
          <span style={{ color: "#fff", fontWeight: 500, fontSize: 15, opacity: 0.85 }}>by. {character.userId || '-'}</span>
        </div>
        {/* 이름, 나이, 직업 */}
        <div style={{ position: "absolute", left: 24, bottom: 44, color: "#fff", textShadow: "0 2px 8px rgba(0,0,0,0.25)" }}>
          <div style={{ fontWeight: 700, fontSize: 28, marginBottom: 4 }}>{character.name}</div>
          <div style={{ fontWeight: 400, fontSize: 17, opacity: 0.92 }}>{character.age ? `${character.age}살` : "나이 비공개"} · {character.job || "직업 비공개"}</div>
        </div>
      </div>
      {/* 해시태그 + 더보기 */}
      <div style={{ padding: "16px 20px 0 20px", display: "flex", gap: 8, flexWrap: "wrap" }}>
        {showTags.map(tag => (
          <span key={tag} style={{ background: "#ffd6ea", color: "#ff4081", borderRadius: 8, padding: "4px 10px", fontWeight: 600, fontSize: 14, marginBottom: 4 }}>#{tag}</span>
        ))}
        {hasMoreTags && !showAllTags && (
          <button onClick={() => setShowAllTags(true)} style={{ background: 'none', border: 'none', color: '#ff4081', fontWeight: 600, fontSize: 14, cursor: 'pointer', marginLeft: 4 }}>더보기 ▼</button>
        )}
        {showAllTags && hasMoreTags && (
          <button onClick={() => setShowAllTags(false)} style={{ background: 'none', border: 'none', color: '#ff4081', fontWeight: 600, fontSize: 14, cursor: 'pointer', marginLeft: 4 }}>접기 ▲</button>
        )}
      </div>
      {/* 캐릭터 한 마디 */}
      <div style={{ padding: "24px 20px 0 20px" }}>
        <div style={{ background: "#ffe3ef", borderRadius: 14, padding: 18, fontWeight: 500, color: "#ff4081", fontSize: 17, textAlign: 'center', marginBottom: 12 }}>
          {character.oneLiner}
        </div>
      </div>
      {/* 탭 */}
      <div style={{ display: "flex", margin: "24px 0 0 0", borderBottom: "1.5px solid #f5b3d7" }}>
        <div onClick={() => setTab('first')} style={{ flex: 1, textAlign: "center", padding: 12, fontWeight: 700, color: tab === 'first' ? "#ff4081" : "#bbb", borderBottom: tab === 'first' ? "2.5px solid #ff4081" : "none", cursor: "pointer" }}>첫 장면</div>
        <div onClick={() => setTab('story')} style={{ flex: 1, textAlign: "center", padding: 12, fontWeight: 700, color: tab === 'story' ? "#ff4081" : "#bbb", borderBottom: tab === 'story' ? "2.5px solid #ff4081" : "none", cursor: "pointer" }}>서사</div>
        <div onClick={() => setTab('community')} style={{ flex: 1, textAlign: "center", padding: 12, fontWeight: 700, color: tab === 'community' ? "#ff4081" : "#bbb", borderBottom: tab === 'community' ? "2.5px solid #ff4081" : "none", cursor: "pointer" }}>커뮤니티</div>
      </div>
      {/* 탭 내용 */}
      <div style={{ padding: "20px" }}>
        {tab === 'first' && (
          <div style={{ background: "var(--color-card)", borderRadius: 12, padding: 16, fontWeight: 500, color: "var(--color-text)", fontSize: 16, textAlign: 'center' }}>{character.background || '등록된 첫 장면이 없습니다.'}</div>
        )}
        {tab === 'story' && (
          <div style={{ background: "var(--color-card)", borderRadius: 12, padding: 16, fontWeight: 500, color: "var(--color-text)", fontSize: 16, textAlign: 'center' }}>{character.firstScene || '등록된 서사가 없습니다.'}</div>
        )}
        {tab === 'community' && (
          <div style={{ background: "var(--color-card)", borderRadius: 12, padding: 32, fontWeight: 700, color: '#ff4081', fontSize: 18, textAlign: 'center' }}>준비중</div>
        )}
      </div>
      {/* 하단 고정 채팅하기 버튼 */}
      <button 
        onClick={handleChatClick}
        style={{
          position: "fixed", 
          left: 0, 
          right: 0, 
          bottom: 0, 
          height: 56, 
          background: "#ff4081", 
          color: "#fff", 
          fontWeight: 700, 
          fontSize: 20, 
          border: "none", 
          borderRadius: 28,
          margin: 12,
          cursor: "pointer",
          boxShadow: "0 2px 8px rgba(0,0,0,0.13)"
        }}
      >
        채팅하기
      </button>
      {/* 페르소나 선택 모달 */}
      {showPersonaModal && (
        <div style={{
          position: "fixed",
          inset: 0,
          background: "rgba(0,0,0,0.5)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 1000
        }}>
          <div style={{
            background: "#fff",
            borderRadius: 20,
            padding: 24,
            width: "90%",
            maxWidth: 400
          }}>
            <div style={{ fontWeight: 700, fontSize: 20, marginBottom: 16 }}>페르소나 선택</div>
            <div style={{ marginBottom: 24 }}>
              <div style={{ 
                background: "#f5f5f5", 
                borderRadius: 12, 
                padding: 16, 
                marginBottom: 12,
                cursor: "pointer",
                border: selectedPersona === "user_74127" ? "2px solid #ff4081" : "none"
              }} onClick={() => setSelectedPersona("user_74127")}>
                <div style={{ fontWeight: 700, fontSize: 16 }}>기본 프로필</div>
                <div style={{ color: "#888", fontSize: 14 }}>user_74127</div>
              </div>
            </div>
            <button 
              onClick={handleStartChat}
              style={{
                width: "100%",
                background: "#ff4081",
                color: "#fff",
                border: "none",
                borderRadius: 12,
                padding: 14,
                fontWeight: 700,
                fontSize: 18,
                cursor: "pointer"
              }}
            >
              채팅하기
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
