import React, { useEffect, useState, useRef } from "react";
import BottomNav from "../components/BottomNav";
import { useNavigate } from "react-router-dom";
import ProfileDetailModal from "../components/ProfileDetailModal";
import DefaultProfileImage from "../components/DefaultProfileImage";
import { FiSettings } from "react-icons/fi";

interface Character {
  id: number;
  profileImg: string;
  name: string;
}

interface Persona {
  id: string;
  name: string;
  avatar: string;
  gender?: string;
  age?: string;
  job?: string;
  info?: string;
  habit?: string;
}

// í”„ë¡œí•„ ì´ë¯¸ì§€ ê²½ë¡œ ìƒìˆ˜ë¡œ ì§€ì •
const DEFAULT_PROFILE_IMG = "/imgdefault.jpg";

function MyPageSkeleton() {
  return (
    <div style={{ padding: "24px 20px 0 20px" }}>
      <div style={{ background: "#f3f3f3", borderRadius: 20, margin: 20, padding: 20, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div style={{ width: 60, height: 60, borderRadius: "50%", background: "#e0e0e0", marginRight: 16, animation: "pulse 1.2s infinite" }} />
        <div style={{ flex: 1 }}>
          <div style={{ width: 120, height: 20, background: "#e0e0e0", borderRadius: 8, marginBottom: 8, animation: "pulse 1.2s infinite" }} />
          <div style={{ width: 80, height: 16, background: "#e0e0e0", borderRadius: 8, animation: "pulse 1.2s infinite" }} />
        </div>
        <div style={{ width: 56, height: 32, background: "#e0e0e0", borderRadius: 16, animation: "pulse 1.2s infinite" }} />
      </div>
      <div style={{ width: 120, height: 18, background: "#e0e0e0", borderRadius: 8, margin: "24px 0 16px 0", animation: "pulse 1.2s infinite" }} />
      <div style={{ background: "#f3f3f3", borderRadius: 16, margin: "16px 0", padding: 16, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div style={{ width: 48, height: 48, borderRadius: "50%", background: "#e0e0e0", marginRight: 16, animation: "pulse 1.2s infinite" }} />
        <div style={{ flex: 1 }}>
          <div style={{ width: 80, height: 16, background: "#e0e0e0", borderRadius: 8, marginBottom: 8, animation: "pulse 1.2s infinite" }} />
        </div>
        <div style={{ width: 40, height: 24, background: "#e0e0e0", borderRadius: 12, animation: "pulse 1.2s infinite" }} />
      </div>
      <style>{`@keyframes pulse { 0% { opacity: 1; } 50% { opacity: 0.5; } 100% { opacity: 1; } }`}</style>
    </div>
  );
}

export default function MyPage() {
  const navigate = useNavigate();
  const [characters, setCharacters] = useState<Character[]>([]);
  const [personas, setPersonas] = useState<Persona[]>([]);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<Persona | null>(null);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showUserProfileEditModal, setShowUserProfileEditModal] = useState(false);
  const userId = "user_74127"; // ì‹¤ì œ ë¡œê·¸ì¸ ê³„ì • idë¡œ ëŒ€ì²´
  const [userProfile, setUserProfile] = useState<{ name: string; avatar: string }>({ name: "user_74127", avatar: "/imgdefault.jpg" });
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [profileEditMode, setProfileEditMode] = useState(false);

  useEffect(() => {
    // ë©€í‹°í”„ë¡œí•„ ëª©ë¡ ë¶ˆëŸ¬ì˜¤ê¸°
    fetch(`/api/persona?userId=${userId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok && data.personas.length > 0) {
          // ê¸°ë³¸ í”„ë¡œí•„(userIdì™€ ê°™ì€ nameì„ ê°€ì§„ ê²ƒ) ì°¾ê¸°
          const defaultPersona = data.personas.find(p => p.userId === userId);
          if (defaultPersona) {
            setUserProfile({ name: defaultPersona.name, avatar: defaultPersona.avatar });
          }
          setPersonas(data.personas);
        }
      });

    // ë‚´ ìºë¦­í„° ëª©ë¡ ë¶ˆëŸ¬ì˜¤ê¸°
    fetch(`/api/character?userId=${userId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setCharacters(data.characters);
      });

    // ì‚¬ìš©ì í”„ë¡œí•„ ì •ë³´ ë¶ˆëŸ¬ì˜¤ê¸° (localStorageì—ì„œ)
    const savedUserProfile = localStorage.getItem('userProfile');
    if (savedUserProfile) {
      const profile = JSON.parse(savedUserProfile);
      setUserProfile({ name: profile.name || "user_74127", avatar: profile.avatar || "/imgdefault.jpg" });
    }
  }, [userId]);

  const handleProfileClick = (profile: Persona) => {
    console.log("handleProfileClick called with:", profile);
    console.log("Setting profileEditMode to true");
    setSelectedProfile(profile);
    setProfileEditMode(true);
    setShowProfileModal(true);
  };

  const handleUserProfileImageClick = () => {
    fileInputRef.current?.click();
  };

  const handleUserProfileImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        const newProfileImg = ev.target?.result as string;
        setUserProfile(prev => ({ ...prev, avatar: newProfileImg }));
        // localStorageì— ì €ì¥
        const userProfileToSave = {
          name: userProfile.name,
          avatar: newProfileImg
        };
        localStorage.setItem('userProfile', JSON.stringify(userProfileToSave));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUserNameSave = () => {
    // localStorageì— ì €ì¥
    const userProfileToSave = {
      name: userProfile.name,
      avatar: userProfile.avatar
    };
    localStorage.setItem('userProfile', JSON.stringify(userProfileToSave));
    setShowUserProfileEditModal(false);
  };

  return (
    <div style={{ background: "var(--color-bg)", minHeight: "100vh", paddingBottom: 80 }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: "24px 20px 0 20px" }}>
        <span style={{ fontWeight: 700, fontSize: 24 }}>MY</span>
        <button
          onClick={() => setShowSettingsModal(true)}
          style={{ background: 'none', border: 'none', cursor: 'pointer', padding: 4 }}
          aria-label="ì„¤ì • ë° ë¡œê·¸ì•„ì›ƒ"
        >
          <FiSettings size={26} color="#fff" />
        </button>
      </div>
      <div style={{
        background: "var(--color-card)",
        borderRadius: 20,
        margin: 20,
        padding: 20,
        boxShadow: "0 2px 8px rgba(0,0,0,0.03)",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between"
      }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={userProfile.avatar || DEFAULT_PROFILE_IMG}
            alt="í”„ë¡œí•„"
            style={{ width: 60, height: 60, borderRadius: "50%", marginRight: 16, objectFit: "cover", cursor: "pointer" }}
            onClick={() => handleProfileClick({
              id: userId,
              name: userProfile.name,
              avatar: userProfile.avatar
            })}
            onError={e => {
              if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                e.currentTarget.onerror = null;
                e.currentTarget.src = "/imgdefault.jpg";
              }
            }}
          />
          <div>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{userProfile.name}</div>
            <div style={{ color: "var(--color-subtext)", fontSize: 15, marginTop: 4 }}>ë³´ìœ  ì¼</div>
          </div>
        </div>
        <div style={{ textAlign: "right" }}>
          <button 
            onClick={() => setShowUserProfileEditModal(true)}
            style={{
              background: "#f5f5f5",
              border: "none",
              borderRadius: 16,
              padding: "8px 18px",
              fontWeight: 600,
              fontSize: 15,
              marginBottom: 16,
              cursor: "pointer"
            }}
          >
            ë‚´ í”„ë¡œí•„
          </button>
          <div style={{ fontWeight: 700, fontSize: 18, color: "#ff4081" }}>35 <span role="img" aria-label="ì¼">ğŸ“</span></div>
        </div>
      </div>
      {/* ë©€í‹°í”„ë¡œí•„ ì„¹ì…˜ - MYì™€ ë‚´ ìºë¦­í„° ì‚¬ì´ */}
      <div style={{ marginTop: 16, padding: "0 20px" }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 8 }}>
          <div style={{ fontWeight: 700, fontSize: 16 }}>ë©€í‹°í”„ë¡œí•„</div>
          <button
            onClick={() => navigate('/foryou')}
            style={{ background: 'none', border: 'none', color: '#bbb', fontWeight: 600, fontSize: 16, cursor: 'pointer', display: 'flex', alignItems: 'center' }}
          >
            <span style={{ fontSize: 22, marginRight: 6 }}>+</span> ë©€í‹°í”„ë¡œí•„ ë§Œë“¤ê¸°
          </button>
        </div>
        {/* ê¸°ë³¸í”„ë¡œí•„(ì‚­ì œX, ìˆ˜ì •O) */}
        {personas.filter(p => p.id === userId).map(p => (
          <div key={p.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card-alt)", borderRadius: 12, padding: 10, marginBottom: 10 }}>
            <img
              src={userProfile.avatar || DEFAULT_PROFILE_IMG}
              alt={userProfile.name}
              style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
              onError={e => {
                if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                  e.currentTarget.onerror = null;
                  e.currentTarget.src = "/imgdefault.jpg";
                }
              }}
            />
            <span style={{ fontWeight: 600, fontSize: 16 }}>{userProfile.name} <span style={{ color: "#ff4081", fontSize: 13, marginLeft: 6 }}>(ê¸°ë³¸)</span></span>
            <button style={{ marginLeft: "auto", color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={() => handleProfileClick({ ...p, name: userProfile.name, avatar: userProfile.avatar })}>ìˆ˜ì •</button>
          </div>
        ))}
        {/* ë‚˜ë¨¸ì§€ ë©€í‹°í”„ë¡œí•„(ìˆ˜ì •/ì‚­ì œO) */}
        {personas.filter(p => p.id !== userId).map(p => (
          <div key={p.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card-alt)", borderRadius: 12, padding: 10, marginBottom: 10 }}>
            <img
              src={p.avatar || DEFAULT_PROFILE_IMG}
              alt={p.name}
              style={{ width: 40, height: 40, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
              onError={e => {
                if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                  e.currentTarget.onerror = null;
                  e.currentTarget.src = "/imgdefault.jpg";
                }
              }}
            />
            <span style={{ fontWeight: 600, fontSize: 16 }}>{p.name}</span>
            <button style={{ marginLeft: "auto", color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={() => handleProfileClick(p)}>ìˆ˜ì •</button>
            <button style={{ color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={async () => {
              if (window.confirm("ì •ë§ë¡œ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?")) {
                await fetch(`/api/persona/${p.id}`, { method: "DELETE" });
                setPersonas(prev => prev.filter(x => x.id !== p.id));
              }
            }}>ì‚­ì œ</button>
          </div>
        ))}
      </div>
      <div style={{ padding: "0 20px", fontWeight: 700, fontSize: 18, marginTop: 24 }}>ë‚´ ìºë¦­í„°</div>
      <div style={{ padding: "0 20px" }}>
        {characters.length === 0 ? (
          <div style={{ color: "var(--color-subtext)" }}>ì•„ì§ ë§Œë“  ìºë¦­í„°ê°€ ì—†ìŠµë‹ˆë‹¤.</div>
        ) : (
          characters.map(char => (
            <div key={char.id} style={{ display: "flex", alignItems: "center", background: "var(--color-card)", borderRadius: 12, padding: 12, marginBottom: 12 }}>
              <img
                src={char.profileImg || DEFAULT_PROFILE_IMG}
                alt={char.name}
                style={{ width: 48, height: 48, borderRadius: "50%", marginRight: 12, objectFit: "cover" }}
                onError={e => {
                  if (!e.currentTarget.src.endsWith("/imgdefault.jpg")) {
                    e.currentTarget.onerror = null;
                    e.currentTarget.src = "/imgdefault.jpg";
                  }
                }}
              />
              <span style={{ fontWeight: 600, fontSize: 16 }}>{char.name}</span>
              <button style={{ marginLeft: "auto", color: "#ff4081", background: "none", border: "none", fontSize: 18, cursor: "pointer" }} onClick={() => navigate(`/character/${char.id}`)}>ìˆ˜ì •</button>
            </div>
          ))
        )}
      </div>
      <BottomNav />

      {/* ìœ ì € í”„ë¡œí•„ ìˆ˜ì • ëª¨ë‹¬ */}
      {showUserProfileEditModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 2000
        }}>
          <div style={{ background: 'var(--color-card)', borderRadius: 20, padding: 32, width: '90%', maxWidth: 400, position: 'relative', boxShadow: '0 2px 16px rgba(0,0,0,0.18)' }}>
            <button
              onClick={() => setShowUserProfileEditModal(false)}
              style={{ position: 'absolute', right: 16, top: 16, background: 'none', border: 'none', fontSize: 24, cursor: 'pointer', color: '#fff' }}
              aria-label="ë‹«ê¸°"
            >Ã—</button>
            <div style={{ fontWeight: 700, fontSize: 22, color: '#fff', marginBottom: 32, textAlign: 'center' }}>í”„ë¡œí•„ ìˆ˜ì •</div>

            {/* í”„ë¡œí•„ ì´ë¯¸ì§€ */}
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
              <div style={{ position: 'relative' }}>
                <img
                  src={userProfile.avatar || DEFAULT_PROFILE_IMG}
                  alt="í”„ë¡œí•„"
