              <div style={{ 
                background: "#f5f5f5", 
                borderRadius: 12, 
                padding: 16, 
                marginBottom: 12,
                cursor: "pointer",
                border: selectedPersona === "user_74127" ? "2px solid #ff4081" : "none"
