            <button 
              onClick={handleStartChat}
              style={{
                width: "100%",
                background: "#ff4081",
                color: "#fff",
                border: "none",
                borderRadius: 12,
                padding: 14,
                fontWeight: 700,
                fontSize: 18,
                cursor: "pointer"
              }}
            >
              채팅하기
            </button>
          </div>
        </div>
      )}
