import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function ChatList() {
  const navigate = useNavigate();
  const [chats, setChats] = useState<any[]>([]);
  const userId = "user_74127"; // 실제 로그인 연동 시 교체

  useEffect(() => {
    fetch(`/api/chat/list?userId=${userId}`)
      .then(res => res.json())
      .then(data => {
        if (data.ok) setChats(data.chats);
      });
  }, []);

  return (
    <div>
      {chats.length === 0 ? (
        <div style={{ color: "#888", textAlign: "center", marginTop: 40 }}>대화 내역이 없습니다.</div>
      ) : (
        chats.map((chat) => (
          <div
            key={chat.characterId}
            onClick={() => navigate(`/chat/${chat.characterId}`)}
            style={{
              display: 'flex',
              alignItems: 'center',
              padding: '16px 0',
              borderBottom: '1px solid #eee',
              cursor: 'pointer',
            }}
          >
            <img
              src={chat.profileImg || "/imgdefault.jpg"}
              alt={chat.name}
              style={{ width: 48, height: 48, borderRadius: '50%', marginRight: 16, objectFit: 'cover' }}
              onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = "/imgdefault.jpg"; }}
            />
            <div style={{ flex: 1 }}>
              <div style={{ fontWeight: 'bold', fontSize: '1.1rem' }}>{chat.name}</div>
              <div style={{ color: '#666', fontSize: '0.95rem', marginTop: 4, whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', maxWidth: 200 }}>{chat.lastMessage}</div>
            </div>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginLeft: 12 }}>
              <img
                src={chat.personaAvatar || "/imgdefault.jpg"}
                alt={chat.personaName || "프로필"}
                style={{ width: 40, height: 40, borderRadius: '50%', objectFit: 'cover', marginBottom: 2 }}
                onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = "/imgdefault.jpg"; }}
              />
              <span style={{ fontSize: 12, color: '#bbb', maxWidth: 60, textAlign: 'center', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>{chat.personaName || '유저프로필'}</span>
            </div>
          </div>
        ))
      )}
    </div>
  );
}
